  input: () => NestFactory.create(MyModule),
  output: "src/api",
  swagger: {
    output: "packages/api/swagger.json",
    servers: [
      {
        url: "http://localhost:37001",
        description: "Local Server",
      },
    ],
    beautify: true,
  },
  distribute: "packages/api",
  primitive: false,
  simulate: true,
};
export default NESTIA_CONFIG;

