import { Controller } from "@nestjs/common";
import { TypedRoute, TypedBody, TypedParam } from "@nestia/core";
import typia from "typia";

import { IShoppingCoupon } from "../../../../api/structures/IShoppingCoupon";
import { IPageIShoppingCoupon } from "../../../../api/structures/IPageIShoppingCoupon";

@Controller("/shoppings/admins/coupons")
export class ShoppingsAdminsCouponsController {
  /**
   * Create a new coupon.
   *
   * Create a new {@link IShoppingCoupon coupon} with given information.
   *
   * By the way, if you are a {@link IShoppingSeller seller}, you have to add
   * include direction's {@link IShoppingCouponSellerCriteria} or
   * {@link IShoppingCouponSaleCriteria} condition. This is because only
   * {@link IShoppingAdministrator administrators} can create a coupon which
   * can be used throughout the market. Seller must limit the usage range by
   * his/her {@link IShoppingSale sale(s)}.
   *
   * Of course, when administrator is planning to make a general coupon that
   * can be used throughout the market, the administrator must get agree from
   * the sellers who are going to be affected.
   *
   * @param body Creation info of the coupon
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Post()
  public async post(
    @TypedBody()
    body: IShoppingCoupon.ICreate,
  ): Promise<IShoppingCoupon> {
    body;
    return typia.random<IShoppingCoupon>();
  }

  /**
   * List up every coupons.
   *
   * List up every {@link IShoppingCoupon coupons} with pagination.
   *
   * If you want, you can limit the result by configuring
   * {@link IShoppingCoupon.IRequest.search search condition} in the request
   * body. Also, it is possible to customize sequence order of records by
   * configuring {@link IShoppingCoupon.IRequest.sort sort condition}.
   *
   * For reference, if you are a {@link IShoppingCustomer customer}, then only
   * {@link IShoppingCouponTicket ticketable} coupons would be listed up.
   * Otherwise, non-ticketable coupons would also be listed up.
   *
   * @param body Request info of pagination, searching and sorting
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Patch()
  public async patch(
    @TypedBody()
    body: IShoppingCoupon.IRequest,
  ): Promise<IPageIShoppingCoupon> {
    body;
    return typia.random<IPageIShoppingCoupon>();
  }

  /**
   * Get a coupon info.
   *
   * Get a {@link IShoppingCoupon coupon} information.
   *
   * If you are a {@link IShoppingCustomer customer}, then only
   * {@link IShoppingCouponTicket ticketable} coupons are accessible. Non
   * ticketable coupons cause 410 gone error. Otherwise you are a
   * {@link IShoppingSeller seller} or
   * {@link IShoppingAdministrator administrator}, non-ticketable coupons are
   * also accessible.
   *
   * @param id Target coupon's {@link IShoppingCoupon.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Get(":id")
  public async getById(
    @TypedParam("id")
    id: string,
  ): Promise<IShoppingCoupon> {
    id;
    return typia.random<IShoppingCoupon>();
  }

  /**
   * Erase a coupon.
   *
   * Erase a {@link IShoppingCoupon coupon} with given ID.
   *
   * For reference, if there're some {@link IShoppingCouponTicket tickets}
   * which are already issued from the target coupon, they would not be
   * affected. Those tickets are still valid until their expration time.
   *
   * @param id Target coupon's {@link IShoppingCoupon.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Delete(":id")
  public async eraseById(
    @TypedParam("id")
    id: string,
  ): Promise<void> {
    id;
    return typia.random<void>();
  }
}

