import { TypedRoute, TypedParam, TypedBody } from "@nestia/core";
import typia, { tags } from "typia";

import { IPageIShoppingSaleSnapshot } from "../../../../../api/structures/IPageIShoppingSaleSnapshot";
import { IPage } from "../../../../../api/structures/IPage";
import { IShoppingSaleSnapshot } from "../../../../../api/structures/IShoppingSaleSnapshot";

@Controller("/shoppings/admins/sales/:saleId/snapshots")
export class ShoppingsAdminsSalesSnapshotsController {
  /**
   * List up every snapshots.
   *
   * Whenever {@link IShoppingSeller seller} updates a
   * {@link IShoppingSale sale}, the sale record is not updated but a new
   * {@link IShoppingSaleSnapshot snapshot} record is created to keep the
   * integrity of the sale history. This API function is for listing up such
   * snapshot records.
   *
   * Also, as you can see from the return type, returned snapshots are
   * summarized, not detailed. If you want to get the detailed information of
   * a snapshot, use {@link at} or {@link flipo} function for each snapshot.
   *
   * For reference, if you're a {@link IShoppingSeller seller}, you can only
   * access to the your own {@link IShoppingSale sale}'s snapshots. Otherwise,
   * you can access to every snapshots of the sales even though the sale has
   * been closed or suspended.
   *
   * @param saleId Target sale's {@link IShoppingSale.id }
   * @param body Requestion info of pagination
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Patch()
  public async patchBySaleid(
    @TypedParam("saleId")
    saleId: string & tags.Format<"uuid">,
    @TypedBody()
    body: IPage.IRequest,
  ): Promise<IPageIShoppingSaleSnapshot.ISummary> {
    saleId;
    body;
    return typia.random<IPageIShoppingSaleSnapshot.ISummary>();
  }

  /**
   * Get a snapshot info.
   *
   * Get a {@link IShoppingSaleSnapshot snapshot} with detailed information.
   *
   * As you can see from the return type, returned snapshot does not contain
   * the {@link IShoppingSale sale} info. If you want to get the sale info, use
   * the {@link flip} function instead.
   *
   * For reference, if you're a {@link IShoppingSeller seller}, you can only
   * access to the your own {@link IShoppingSale sale}'s snapshots. Otherwise,
   * you can access to every snapshots of the sales even though the sale has
   * been closed or suspended.
   *
   * @param saleId Belonged sale's {@link IShoppingSale.id }
   * @param id Target snapshot's {@link IShoppingSaleSnapshot.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Get(":id")
  public async getBySaleidAndId(
    @TypedParam("saleId")
    saleId: string & tags.Format<"uuid">,
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
  ): Promise<IShoppingSaleSnapshot> {
    saleId;
    id;
    return typia.random<IShoppingSaleSnapshot>();
  }
}

