import { Controller } from "@nestjs/common";
import { TypedRoute, TypedParam, TypedBody } from "@nestia/core";
import typia, { tags } from "typia";

import { IShoppingChannelCategory } from "../../../../../../api/structures/IShoppingChannelCategory";

@Controller("/shoppings/admins/systematic/channels/:channelCode/categories")
export class ShoppingsAdminsSystematicChannelsCategoriesController {
  /**
   * Create a new category.
   *
   * Create a new {@link IShoppingChannelCategory category} of a
   * {@link IShoppingChannel channel} with given name. If required, it is
   * possible to specify the parent category by its ID.
   *
   * @param channelCode Belonged channel's {@link IShoppingChannel.code }
   * @param body Creation info of the category
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Post()
  public async postByChannelcode(
    @TypedParam("channelCode")
    channelCode: string,
    @TypedBody()
    body: IShoppingChannelCategory.ICreate,
  ): Promise<IShoppingChannelCategory> {
    channelCode;
    body;
    return typia.random<IShoppingChannelCategory>();
  }

  /**
   * List up every categories with children records.
   *
   * List up every {@link IShoppingChannelCategory.IHierarchical categories} of
   * a {@link IShoppingChannel channel} with pagination. Returned categories
   * contain children categories, too.
   *
   * If you want, you can limit the result by configuring
   * {@link IShoppingChannelCategory.IRequest.search search condition} in the
   * request body. Also, it is possible to customize sequence order of records
   * by configuring
   * {@link IShoppingChannelCategory.IRequest.sort sort condition}.
   *
   * @param channelCode Belonged channel's {@link IShoppingChannel.code }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Patch()
  public async patchByChannelcode(
    @TypedParam("channelCode")
    channelCode: string,
  ): Promise<void> {
    channelCode;
    return typia.random<void>();
  }

  /**
   * Get a category info.
   *
   * Get a detailed {@link IShoppingChannelCategory category} information.
   *
   * Returned category contains hierarchical children categories, and also
   * contains the recursive parent categories, too.
   *
   * @param channelCode Belonged channel's {@link IShoppingChannel.code }
   * @param id Target category's {@link IShoppingChannelCategory.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Get(":id")
  public async getByChannelcodeAndId(
    @TypedParam("channelCode")
    channelCode: string,
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
  ): Promise<IShoppingChannelCategory> {
    channelCode;
    id;
    return typia.random<IShoppingChannelCategory>();
  }

  /**
   * Update a category.
   *
   * Update a {@link IShoppingChannelCategory category}'s name. If required, it
   * is possible to change the parent category by its ID. Of course, detaching
   * from the parent category so that becoming the root category is also
   * possible.
   *
   * @param channelCode Belonged channel's {@link IShoppingChannel.code }
   * @param id Target category's {@link IShoppingChannelCategory.id }
   * @param body Update info of the category
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Put(":id")
  public async putByChannelcodeAndId(
    @TypedParam("channelCode")
    channelCode: string,
    @TypedParam("id")
    id: string,
    @TypedBody()
    body: IShoppingChannelCategory.ICreate,
  ): Promise<void> {
    channelCode;
    id;
    body;
    return typia.random<void>();
  }
}

import { Controller } from "@nestjs/common";
import { TypedRoute, TypedParam, TypedBody } from "@nestia/core";
import typia from "typia";

import { IRecordMerge } from "../../../../../../../api/structures/IRecordMerge";

@Controller(
  "/shoppings/admins/systematic/channels/:channelCode/categories/merge",
)
export class ShoppingsAdminsSystematicChannelsCategoriesMergeController {
  /**
   * Merge multiple categories into one.
   *
   * In this shopping mall system, it is not possible to delete a
   * {@link IShoppingChannelCategory category}, because it is a systematic
   * entity affecting to all other core entities like
   * {@link IShoppingSale sales}. Instead of deleting, you can merge multiple
   * categories into one.
   *
   * If you specify a category to absorb others, then all of other categories
   * will be merged into the specified one. Also, subsidiary entities of
   * categories also be merged and their references also be merged
   * cascadingly.
   *
   * @param channelCode Belonged channel's {@link IShoppingChannel.code }
   * @param body Merge info of the categories
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Delete()
  public async eraseByChannelcode(
    @TypedParam("channelCode")
    channelCode: string,
    @TypedBody()
    body: IRecordMerge,
  ): Promise<void> {
    channelCode;
    body;
    return typia.random<void>();
  }
}

