import { Controller } from "@nestjs/common";
import { TypedRoute, TypedBody } from "@nestia/core";
import typia from "typia";

import { IShoppingCustomer } from "../../../../api/structures/IShoppingCustomer";

@Controller("/shoppings/customers/authenticate")
export class ShoppingsCustomersAuthenticateController {
  /**
   * Get current customer info.
   *
   * Get current {@link IShoppingCustomer customer} information from the
   * {@link IShoppingCustomer.IToken.access access token}.
   *
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Get()
  public async get(): Promise<IShoppingCustomer> {
    return typia.random<IShoppingCustomer>();
  }

  /**
   * Create a new customer record.
   *
   * This shopping mall system defines everyone participating in this market
   * as a "customer". And the customer records are not archived based on
   * individual {@link IShoppingCitizen people}, but based on the unit of
   * connection. Therefore, even if it is the same person, a new
   * {@link IShoppingCustomer} record is created every time a connection is
   * made.
   *
   * Therefore, all Client Applications that access this service must first
   * call this function, report the customer's inflow path to the server, and
   * create an {@link IShoppingCustomer.IToken.access access token}. If you
   * skip this function call, all the other API functions would be prohibited.
   * There is no exception, even if you want to {@link activate} your
   * citizenship or {@link login} with your
   * {@link IShoppingMember member account}. Before authenticating yourself or
   * logging in, be sure to call this function first. This also applies when
   * an {@link IShoppingAdministrator administrator} or
