import { Controller } from "@nestjs/common";
import { TypedRoute, TypedBody, TypedParam } from "@nestia/core";
import typia, { tags } from "typia";

import { IShoppingCartCommodity } from "../../../../../api/structures/IShoppingCartCommodity";
import { IPageIShoppingCartCommodity } from "../../../../../api/structures/IPageIShoppingCartCommodity";

@Controller("/shoppings/customers/carts/commodities")
export class ShoppingsCustomersCartsCommoditiesController {
  /**
   * Create a new commodity.
   *
   * Create a new {@link IShoppingCartCommodity commodity} into a specific
   * shopping cart.
   *
   * If {@link IShoppingCartCommodity.ICreate.accumulate} has `true` value and
   * there's some same commodity that composed with same
   * {@link IShoppingSaleUnitStock.IInvert stocks and quantities}, then new
   * commodity would not be created but the volume would be accumulated.
   *
   * By the way, if the target {@link IShoppingSale sale} has been suspended or
   * {@link IShoppingSaleUnitStockInventory out of stock}, then 410 gone error
   * would be thrown. Therefore, it would better to check the target sale and
   * {@link IShoppingSaleUnitStock stock}'s status before.
   *
   * @param body Creation info of the commodity
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Post()
  public async post(
    @TypedBody()
    body: IShoppingCartCommodity.ICreate,
  ): Promise<IShoppingCartCommodity> {
    body;
    return typia.random<IShoppingCartCommodity>();
  }

  /**
   * List of every commodities.
   *
   * List up every {@link IShoppingCartCommodity commodities} in the shopping
   * cart with {@link IPage pagination}.
   *
   * You can limit the result by configuring
   * {@link IShoppingCartCommodity.IRequest.search search condition} in the
   * request body. Furthermore, it is possible to customize sequence order of
   * records by configuring {@link IShoppingCartCommodity.IRequest.sort}.
   *
   * For reference, when some commodity be {@link IShoppingOrder ordered} and
   * {@link IShoppingOrderPublish published}, then it would not be appread in
   * the shopping cart more. Otherwise, the order has not been published yet,
   * it would be appread in the shopping cart and still enable to create a new
   * {@link IShoppingOrder order application} with the same commodity.
   *
   * Of course, if the target {@link IShoppingSale sale} has been suspended, or
   * {@link IShoppingSaleUnitStockInventory out of stock}, then it would not be
   * appread in the shopping cart more, either.
   *
   * @param body Request info of pagination, searching and sorting
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Patch()
  public async patch(
    @TypedBody()
    body: IShoppingCartCommodity.IRequest,
  ): Promise<IPageIShoppingCartCommodity> {
    body;
    return typia.random<IPageIShoppingCartCommodity>();
  }

  /**
   * Get a commodity.
   *
   * Get a {@link IShoppingCartCommodity commodity} record of the shopping
   * cart.
   *
   * If target {@link IShoppingSale sale} has been suspended or
   * {@link IShoppingSaleUnitStockInventory out of stock}, then 410 gone error
   * would be thrown. Therefore, even if you've created a commodity
   * successfully with the {@link create} method, it still can be failed when
   * you access the commodity with this {@link at} method.
   *
   * @param id Target commodity's {@link IShoppingCartCommodity.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Get(":id")
  public async getById(
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
  ): Promise<IShoppingCartCommodity> {
    id;
    return typia.random<IShoppingCartCommodity>();
  }

  /**
   * Update a commodity (volume).
   *
   * Update a {@link IShoppingCartCommodity commodity}'s volume in the shopping
   * cart.
   *
   * If target {@link IShoppingSale sale} has been suspended or
   * {@link IShoppingSaleUnitStockInventory out of stock} suddenly, then 410
   * gone error would be thrown, either.
   *
   * @param id Target commodity's {@link IShoppingCartCommodity.id }
   * @param body Update info of the commodity (volume)
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Put(":id")
  public async putById(
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
    @TypedBody()
    body: IShoppingCartCommodity.IUpdate,
  ): Promise<void> {
    id;
    body;
    return typia.random<void>();
  }

  /**
   * Erase a commodity.
   *
   * Erase a {@link IShoppingCartCommodity commodity} from the shopping cart.
   *
   * If the commodity is on an {@link IShoppingOrder order} process, it is not
   * possible to erase it. Instead, if the order has been
   * {@link IShoppingOrderPublish published}, then it would not be appread in
   * the shopping cart more. If the order be erased, then you also can
   * continue erasinng the commodity, neither.
   *
   * @param id Target commodity's {@link IShoppingCartCommodity.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Delete(":id")
  public async eraseById(
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
  ): Promise<void> {
    id;
    return typia.random<void>();
  }
}

