import { Controller } from "@nestjs/common";
import { TypedRoute, TypedBody, TypedParam } from "@nestia/core";
import typia, { tags } from "typia";

import { IShoppingOrder } from "../../../../api/structures/IShoppingOrder";
import { IPageIShoppingOrder } from "../../../../api/structures/IPageIShoppingOrder";

@Controller("/shoppings/customers/orders")
export class ShoppingsCustomersOrdersController {
  /**
   * Create a new order application.
   *
   * Create a new {@link IShoppingOrder order application} from a
   * {@link IShoppingCartCommodity shopping cart} that has been composed by the
   * {@link IShoppingCustomer}. Of course, do not need to put every commodities
   * to the order, but possible to select some of them by the customer.
   *
   * By the way, this function does not mean completion the order, but means
   * just customer is applying the order. The order be completed only when
   * customer {@link IShoppingOrderPublish.paid_at pays} the order.
   *
   * > If you are an A.I. chatbot, don't take a mistake that writing the
   * > `commodity_id` with the user selected stock ID. The `commodity_id` is the
   * > ID of the shopping cart commodity, not the stock ID. The stock ID is
   * > already included in the shopping cart commodity.
   *
   * @param body Creation info of the order
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Post()
  public async post(
    @TypedBody()
    body: IShoppingOrder.ICreate,
  ): Promise<IShoppingOrder> {
    body;
    return typia.random<IShoppingOrder>();
  }

  /**
   * List up every orders.
   *
   * List up every {@link IShoppingOrder orders} with pagination.
   *
   * If you want, you can limit the result by configuring
   * {@link IShoppingOrder.IRequest.search search condition} in the request
   * body. Also, it is possible to customize sequence order of records by
   * configuring {@link IShoppingOrder.IRequest.sort sort condition}.
   *
   * For reference, if you are a {@link IShoppingCustomer customer}, then you
   * can list up your own orders, and it is not a matter whether the order has
   * been {@link IShoppingOrderPublish.paid_at paid} or not.
   *
   * Otherwise you are a {@link IShoppingSeller seller} or
   * {@link IShoppingAdministrator administrator}, then you can list up only
   * paid orders. Also, in the seller case, only related
   * {@link IShoppingOrder.goods goods} would be listed up in the order.
   *
   * @param body Request info of pagination, searching and sorting
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Patch()
  public async patch(
    @TypedBody()
    body: IShoppingOrder.IRequest,
  ): Promise<IPageIShoppingOrder> {
    body;
    return typia.random<IPageIShoppingOrder>();
  }

  /**
   * Get an order info.
   *
   * Get a detailed {@link IShoppingOrder order} information.
   *
   * If you are not a {@link IShoppingCustomer customer}, then you can't access
   * to the order which has not been {@link IShoppingOrderPublish.paid_at paid}
   * yet. In that case, 404 not found error would be thrown.
   *
   * @param id Target order's {@link IShoppingOrder.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Get(":id")
  public async getById(
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
  ): Promise<IShoppingOrder> {
    id;
    return typia.random<IShoppingOrder>();
  }

  /**
   * Erase an order application.
   *
   * Erase an order application that has been applied by the
   * {@link IShoppingCustomer}.
   *
   * If the order has been {@link IShoppingOrderPublish published}, then it is
   * not possible to erase the order. In that case, you've to cancel the
   * payment by calling the {@link publish.cancel} function.
   *
   * @param id Target order's {@link IShoppingOrder.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Delete(":id")
  public async eraseById(
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
  ): Promise<void> {
    id;
    return typia.random<void>();
  }
}

