import { Controller } from "@nestjs/common";
import { TypedRoute, TypedBody, TypedParam } from "@nestia/core";
import typia, { tags } from "typia";

import { IShoppingSale } from "../../../../api/structures/IShoppingSale";
import { IPageIShoppingSale } from "../../../../api/structures/IPageIShoppingSale";
import { IShoppingSaleSnapshot } from "../../../../api/structures/IShoppingSaleSnapshot";

@Controller("/shoppings/sellers/sales")
export class ShoppingsSellersSalesController {
  /**
   * Create a sale.
   *
   * {@link IShoppingSeller Seller} creates a new {@link IShoppingSale} for
   * operation.
   *
   * For reference, sale has complicate hierarchical structure that composing
   * with {@link IShoppingSaleUnit units},
   * {@link IShoppingSaleUnitOption options} and
   * {@link IShoppingSaleUnitStock stocks}. Therefore, I recommend you to read
   * the {@link IShoppingSale} and related DTOs' documents before creating a
   * new sale.
   *
   * ERD (Entity Relationship Diagram) and its description document also would
   * be helpful, too.
   *
   * @param body Creation info of the sale
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Post()
  public async post(
    @TypedBody()
    body: IShoppingSale.ICreate,
  ): Promise<IShoppingSale> {
    body;
    return typia.random<IShoppingSale>();
  }

  /**
   * List up every summarized sales.
   *
   * List up every {@link IShoppingSale.ISummary summarized sales}.
   *
   * As you can see, returned sales are summarized, not detailed. It does not
   * contain the SKU (Stock Keeping Unit) information represented by the
   * {@link IShoppingSaleUnitOption} and {@link IShoppingSaleUnitStock} types.
   * If you want to get such detailed information of a sale, use `GET
   * /shoppings/customers/sales/{id}` operation for each sale.
   *
   * > If you're an A.I. chatbot, and the user wants to buy or compose
   * > {@link IShoppingCartCommodity shopping cart} from a sale, please call the
   * > `GET /shoppings/customers/sales/{id}` operation at least once to the
   * > target sale to get detailed SKU information about the sale. It needs to
   * > be run at least once for the next steps.
   *
   * @param body Request info of pagination, searching and sorting
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Patch()
  public async patch(
    @TypedBody()
    body: IShoppingSale.IRequest,
  ): Promise<IPageIShoppingSale.ISummary> {
    body;
    return typia.random<IPageIShoppingSale.ISummary>();
  }

  /**
   * Get a sale with detailed information.
   *
   * Get a {@link IShoppingSale sale} with detailed information including the
   * SKU (Stock Keeping Unit) information represented by the
   * {@link IShoppingSaleUnitOption} and {@link IShoppingSaleUnitStock} types.
   *
   * > If you're an A.I. chatbot, and the user wants to buy or compose a
   * > {@link IShoppingCartCommodity shopping cart} from a sale, please call this
   * > operation at least once to the target sale to get detailed SKU
   * > information about the sale.
   *
   * > It needs to be run at least once for the next steps. In other words, if
   * > you A.I. agent has called this operation to a specific sale, you don't
   * > need to call this operation again for the same sale.
   *
   * > Additionally, please do not summarize the SKU information. Just show the
   * > every options and stocks in the sale with detailed information.
   *
   * @param id Target sale's {@link IShoppingSale.id }
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Get(":id")
  public async getById(
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
  ): Promise<IShoppingSale> {
    id;
    return typia.random<IShoppingSale>();
  }

  /**
   * Update a sale.
   *
   * Update a {@link IShoppingSale sale} with new information.
   *
   * By the way, the sale actually does not being modified, but just make a
   * new {@link IShoppingSaleSnapshot snapshot} record of the sale. Its 1st
   * purpose is to keeping the integrity of the sale, due to modification of
   * the sale must not affect to the {@link IShoppingOrder orders} that already
   * had been applied to the sale.
   *
   * The 2nd purpose is for the A/B tests. {@link IShoppingSeller Seller} needs
   * to demonstrate operating performance by changing price, content, and
   * composition of the product. This snapshot concept would be helpful for
   * it.
   *
   * @param id Target sale's {@link IShoppingSale.id }
   * @param body New information of the sale
   * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
   */
  @TypedRoute.Put(":id")
  public async putById(
    @TypedParam("id")
    id: string & tags.Format<"uuid">,
    @TypedBody()
    body: IShoppingSaleSnapshot.ICreate,
  ): Promise<IShoppingSale> {
    id;
    body;
    return typia.random<IShoppingSale>();
  }
}

