import { tags } from "typia";

import { IShoppingMember } from "./IShoppingMember";
import { IShoppingCitizen } from "./IShoppingCitizen";
import { IShoppingChannel } from "./IShoppingChannel";
import { IShoppingExternalUser } from "./IShoppingExternalUser";

/**
 * Customer information, but not a person but a connection basis.
 *
 * `IShoppingCustomer` is an entity that literally embodies the information of
 * those who participated in the market as customers. By the way, the
 * `IShoppingCustomer` does not mean a person, but a connection basis.
 * Therefore, even if the same person connects to the shopping mall multiple,
 * multiple records are created in `IShoppingCustomer`.
 *
 * The first purpose of this is to track the customer's inflow path in detail,
 * and it is for cases where the same person enters as a non-member,
 * {@link IShoppingCartCommodity puts items in the shopping cart} in advance, and
 * only authenticates their {@link IShoppingCitizen real name} or registers/logs
 * in at the moment of {@link IShoppingOrderPublish payment}. It is the second.
 * Lastly, it is to accurately track the activities that a person performs at
 * the shopping mall in various ways like below.
 *
 * - Same person comes from an {@link IShoppingExternalUser external service}
 * - Same person creates multiple accounts
 * - Same person makes a {@link IShoppingOrderPublish purchase} as a non-member
 *   with only {@link IShoppingCitizen real name authentication}
 * - Same person acts both {@link IShoppingSeller seller} and
 *   {@link IShoppingAdministrator admin} at the same time
 *
 * Therefore, `IShoppingCustomer` can have multiple records with the same
 * {@link IShoppingCitizen}, {@link IShoppingMember}, and
 * {@link IShoppingExternalUser}. Additionally, if a customer signs up for
 * membership after verifying their real name or signs up for our service after
 * being a user of an external service, all related records are changed at once.
 * Therefore, identification and tracking of customers can be done very
 * systematically.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type IShoppingCustomer = {
  /**
   * Discriminant for the type of customer.
   *
   * @title Discriminant for the type of customer
   */
  type: "customer";

  /**
   * Membership information.
   *
   * If the customer has joined as a member.
   *
   * @title Membership information
   */
  member: null | IShoppingMember;

  /**
   * Citizen information.
   *
   * If the customer has verified his real name and mobile number.
   *
   * @title Citizen information
   */
  citizen: null | IShoppingCitizen;

  /**
   * Primary Key.
   *
   * @title Primary Key
   */
  id: string & tags.Format<"uuid">;

  /**
   * Belonged channel.
   *
   * @title Belonged channel
   */
  channel: IShoppingChannel;

  /**
   * External user information.
   *
   * When the customer has come from an external service.
   *
   * @title External user information
   */
  external_user: null | IShoppingExternalUser;

  /**
   * Connection address.
   *
   * Same with {@link window.location.href} of client.
   *
   * @title Connection address
   */
  href: string & tags.Format<"uri">;

  /**
   * Referrer address.
   *
   * Same with {@link window.document.referrer} of client.
   *
   * @title Referrer address
   */
  referrer: null | (string & tags.Format<"uri">) | (string & tags.MaxLength<0>);

  /**
   * Connection IP Address.
   *
   * @title Connection IP Address
   */
  ip: (string & tags.Format<"ipv4">) | (string & tags.Format<"ipv6">);

  /**
   * Creation time of the connection record.
   *
   * @title Creation time of the connection record
   */
  created_at: string & tags.Format<"date-time">;
};
export namespace IShoppingCustomer {
  /**
   * Inverted customer information.
   *
   * This inverted customer information has been designed to be used for
   * another invert information of sellers and administrators like below.
   *
   * - {@link IShoppingSeller.IInvert}
   * - {@link IShoppingAdministrator.IInvert}
   */
  export type IInvert = {
    /**
     * Primary Key.
     *
     * @title Primary Key
     */
    id: string & tags.Format<"uuid">;

    /**
     * Belonged channel.
     *
     * @title Belonged channel
     */
    channel: IShoppingChannel;

    /**
     * External user information.
     *
     * When the customer has come from an external service.
     *
     * @title External user information
     */
    external_user: null | IShoppingExternalUser;

    /**
     * Connection address.
     *
     * Same with {@link window.location.href} of client.
     *
     * @title Connection address
     */
    href: string & tags.Format<"uri">;

    /**
     * Referrer address.
     *
     * Same with {@link window.document.referrer} of client.
     *
     * @title Referrer address
     */
    referrer:
      | null
      | (string & tags.Format<"uri">)
      | (string & tags.MaxLength<0>);

    /**
     * Connection IP Address.
     *
     * @title Connection IP Address
     */
    ip: (string & tags.Format<"ipv4">) | (string & tags.Format<"ipv6">);

    /**
     * Creation time of the connection record.
     *
     * @title Creation time of the connection record
     */
    created_at: string & tags.Format<"date-time">;
  };

  export type IAuthorized = {
