      "IShoppingSaleSnapshot": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string",
            "format": "uuid",
            "title": "Primary Key of Sale",
            "description": "Primary Key of Sale."
          },
          "snapshot_id": {
