import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingDelivery } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDelivery";
import { IShoppingDeliveryJourney } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDeliveryJourney";
import { IShoppingDeliveryPiece } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDeliveryPiece";
import { IShoppingDeliveryShipper } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDeliveryShipper";

export async function test_api_create_delivery_journey_with_valid_and_invalid_data(connection: api.IConnection): Promise<void> {
    // 1.  :     
    const deliveryInput = {
        pieces: [
            {
                publish_id: "00000000-0000-0000-0000-000000000001",
                good_id: "00000000-0000-0000-0000-000000000002",
                stock_id: "00000000-0000-0000-0000-000000000003",
                quantity: 1
            },
        ],
        journeys: [
            {
                type: "preparing",
                title: "Preparation",
                description: "Preparing goods",
                started_at: new Date().toISOString(),
                completed_at: null,
            }
        ],
        shippers: [
            {
                company: "",
                name: "",
                mobile: "01012345678"
            }
        ]
    };

    const delivery: IShoppingDelivery = await api.functional.shoppings.sellers.deliveries.post(connection, deliveryInput);
    typia.assert<IShoppingDelivery>(delivery);

    //  valid  journey 
    const journeyInput = {
        type: "manufacturing",
        title: "",
        description: " ",
        started_at: new Date().toISOString(),
        completed_at: null
    };
    const journey = await api.functional.shoppings.sellers.deliveries.journeys.post(connection, delivery.id, journeyInput);
    typia.assert<IShoppingDeliveryJourney>(journey);
    TestValidator.equals("journey type")(journeyInput.type)(journey.type);
    TestValidator.equals("journey title")(journeyInput.title)(journey.title);
    TestValidator.equals("journey description")(journeyInput.description)(journey.description);

    // 2.    - type 
    const invalidJourneyInput = Object.assign({}, journeyInput);
    // @ts-expect-error   
    delete invalidJourneyInput.type;
    await TestValidator.httpError("type   400")(400)(async () => {
        await api.functional.shoppings.sellers.deliveries.journeys.post(connection, delivery.id, invalidJourneyInput as any);
    });

    // 2.    - type  
    const wrongTypeJourneyInput = { ...journeyInput, type: "unknown" };
    await TestValidator.httpError("type   400")(400)(async () => {
        await api.functional.shoppings.sellers.deliveries.journeys.post(connection, delivery.id, wrongTypeJourneyInput as any);
    });

    // 3.   journey  
    const arrivedDeliveryInput = {
        pieces: [
            {
                publish_id: "00000000-0000-0000-0000-000000000011",
                good_id: "00000000-0000-0000-0000-000000000012",
                stock_id: "00000000-0000-0000-0000-000000000013",
                quantity: 1
            },
        ],
        journeys: [
            {
                type: "delivering",
                title: "",
                description: " ",
                started_at: new Date().toISOString(),
                completed_at: new Date().toISOString(), // completed_at not null -> arrived 
            }
        ],
        shippers: [
            {
                company: "",
                name: "",
                mobile: "01087654321"
            }
        ]
    };
    const arrivedDelivery: IShoppingDelivery = await api.functional.shoppings.sellers.deliveries.post(connection, arrivedDeliveryInput);
    typia.assert<IShoppingDelivery>(arrivedDelivery);
    //   (delivery)  arrived  

    const addJourneyInput = {
        type: "delivering",
        title: "",
        description: "   ",
        started_at: new Date().toISOString(),
        completed_at: null
    };
    await TestValidator.httpError("arrived   409")(409)(async () => {
        await api.functional.shoppings.sellers.deliveries.journeys.post(connection, arrivedDelivery.id, addJourneyInput);
    });
}

