import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSection } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSection";
import { IRecordMerge } from "@ORGANIZATION/PROJECT-api/lib/structures/IRecordMerge";
import typia from "typia";

export async function test_merge_multiple_sections_valid_and_invalid(
    connection: api.IConnection,
): Promise<void> {
    // 1.  2  
    const section1 = await api.functional.shoppings.admins.systematic.sections.post(connection, { code: "fruit-corner", name: "Fruit Corner" });
    const section2 = await api.functional.shoppings.admins.systematic.sections.post(connection, { code: "butcher-corner", name: "Butcher Corner" });
    typia.assert<IShoppingSection>(section1);
    typia.assert<IShoppingSection>(section2);

    // 2.  : section1 section2 absorbed 
    const mergeInput: IRecordMerge = {
        keep: section1.id,
        absorbed: [section2.id],
    };
    await api.functional.shoppings.admins.systematic.sections.merge(connection, mergeInput);

    // 3.   keep   
    const keptSection = await api.functional.shoppings.admins.systematic.sections.at(connection, section1.id);
    typia.assert<IShoppingSection>(keptSection);
    TestValidator.equals("keep section still valid")<IShoppingSection>(section1)(keptSection);

    // 4. () section2  404   
    await TestValidator.httpError("absorbed section must not exist after merge")(404)(
        () => api.functional.shoppings.admins.systematic.sections.at(connection, section2.id)
    );

    // 5.   id merge  
    const nonExistId = "00000000-0000-4000-8000-000000000001";
    const invalidInput: IRecordMerge = {
        keep: section1.id,
        absorbed: [nonExistId],
    };
    await TestValidator.httpError("merging with non-existing section id must fail")(404)(
        () => api.functional.shoppings.admins.systematic.sections.merge(connection, invalidInput)
    );

    // 6.   section2      
    const repeatMergeInput: IRecordMerge = {
        keep: section1.id,
        absorbed: [section2.id],
    };
    await TestValidator.httpError("merging already-absorbed section id must fail")(404)(
        () => api.functional.shoppings.admins.systematic.sections.merge(connection, repeatMergeInput)
    );
}

