import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingCustomer } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCustomer";

export async function test_refresh_token_with_valid_and_invalid_token(connection: api.IConnection): Promise<void> {
    // 1.       
    const createInput: IShoppingCustomer.ICreate = {
        channel_code: "default-channel",
        href: "https://localhost/path",
        referrer: null,
        ip: "127.0.0.1",
        external_user: null,
    };
    const authorized = await api.functional.shoppings.customers.authenticate.post(connection, { body: createInput });
    typia.assert<IShoppingCustomer.IAuthorized>(authorized);
    const oldToken = authorized.token;

    // 2.    
    const refreshInput: IShoppingCustomer.IRefresh = {
        value: oldToken.refresh,
    };
    const refreshed = await api.functional.shoppings.customers.authenticate.refresh.patch(connection, { body: refreshInput });
    typia.assert<IShoppingCustomer.IAuthorized>(refreshed);
    TestValidator.predicate("access/refresh are newly issued")(refreshed.token.access !== oldToken.access && refreshed.token.refresh !== oldToken.refresh);
    TestValidator.predicate("type is customer")(refreshed.type === "customer");

    // 3.   -   
    const badRefresh: IShoppingCustomer.IRefresh = { value: "__invalid_token__" };
    await TestValidator.httpError("invalid refresh token should fail")(401, 403)(
        () => api.functional.shoppings.customers.authenticate.refresh.patch(connection, { body: badRefresh })
    );

    // 4. () refresh     (refreshable_until  ,     ,        )
    const expiredRefresh: IShoppingCustomer.IRefresh = { value: "eyJhbGciOi...expired" };
    await TestValidator.httpError("expired refresh token should fail")(401, 403)(
        () => api.functional.shoppings.customers.authenticate.refresh.patch(connection, { body: expiredRefresh })
    );
}

