import { IShoppingCustomer as IShoppingCustomerNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCustomer";
import typia from "typia";

export async function test_api_get_current_customer_info_authenticated_and_unauthenticated(connection: api.IConnection): Promise<void> {
    // 1. Create a random IShoppingCustomer.ICreate input
    const customerInput: IShoppingCustomerNS.ICreate = {
        channel_code: "main-channel",
        external_user: null,
        href: "https://shop.example.com/onboarding",
        referrer: null,
        ip: null
    };

