import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingMember } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingMember";
import { IShoppingCustomer } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCustomer";
import { IShoppingMember as IJoin, IShoppingMember } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingMember";
import { IShoppingCitizen } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCitizen";
import typia from "typia";

export async function test_join_membership_success_and_duplicate_and_missing_fields(
  connection: api.IConnection,
): Promise<void> {
  // 1.   
  const joinInput = {
    nickname: `user_${Date.now()}`,
    email: `test_${Date.now()}@example.com`,
    password: "TestPassword1!",
    citizen: {
      name: "",
      mobile: "01012341234",
    },
  };

  const input: IJoin.IJoin = {
    nickname: joinInput.nickname,
    email: joinInput.email,
    password: joinInput.password,
    citizen: joinInput.citizen,
  };

  const joined = await api.functional.shoppings.customers.authenticate.join(
    connection,
    input,
  );
  typia.assert<IShoppingCustomer>(joined);
  TestValidator.equals("nickname match")(input.nickname)((joined.member && joined.member.nickname) || "");
  TestValidator.predicate("joined id exists")(() => Boolean(joined.id));

  // 2.     ->  
  await TestValidator.httpError("duplicate join should fail")(409)(async () => {
    await api.functional.shoppings.customers.authenticate.join(connection, input);
  });

  // 3.   
  const requiredFields: Array<keyof IJoin.IJoin> = [
    "nickname",
    "email",
    "password",
    "citizen",
  ];
  for (const field of requiredFields) {
    const invalidInput = { ...input };
    // @ts-ignore
    delete invalidInput[field];
    await TestValidator.httpError(`Missing required field: ${field}`)(400)(async () => {
      await api.functional.shoppings.customers.authenticate.join(
        connection,
        invalidInput as any,
      );
    });
  }
