import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingDepositCharge } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDepositCharge";
import { IShoppingDepositCharge as IShoppingDepositChargeNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDepositCharge";
import typia from "typia";

export async function test_update_deposit_charge_application_by_owner_and_not_owner(
  ownerConnection: api.IConnection,
  notOwnerConnection: api.IConnection
): Promise<void> {
  // 1. Owner creates a deposit charge application
  const chargeInput: IShoppingDepositChargeNS.ICreate = {
    value: 10000,
  };

  const created: IShoppingDepositCharge = await api.functional.shoppings.customers.deposits.charges.post(
    ownerConnection,
    { body: chargeInput }
  );
  typia.assert<IShoppingDepositCharge>(created);
  TestValidator.equals("created.value")<number>(chargeInput.value)(created.value);

  // 2. Owner updates the deposit charge
  const updateInput: IShoppingDepositChargeNS.ICreate = {
    value: 30000,
  };
  let updateError: Error | null = null;
  let updated: IShoppingDepositCharge | null = null;
  try {
    updated = await api.functional.shoppings.customers.deposits.charges.put(
      ownerConnection,
      created.id,
      { body: updateInput }
    );
  } catch (err) {
    updateError = err as Error;
  }

  // 2a. Check if update success or rejected due to "published"
  if (updated) {
    typia.assert<IShoppingDepositCharge>(updated);
    TestValidator.equals("updated.value")<number>(updateInput.value)(updated.value);
    TestValidator.equals("id stays same")(created.id)(updated.id);
  } else {
    TestValidator.predicate("Update failed due to publication or error")(
      () => !!updateError && updateError instanceof Error
    );
  }

  // 3. Not owner attempts update and must get error
  await TestValidator.error("should not allow not-owner to update deposit charge")(
    async () => {
      await api.functional.shoppings.customers.deposits.charges.put(
        notOwnerConnection,
        created.id,
        { body: updateInput }
      );
    }
  );
}

