  connection: api.IConnection,
  saleId: string,
  inquiryId: string,
  anotherSellerConnection: api.IConnection,
): Promise<void> {
  // 1.   (  )
  const basicInput: Partial<IShoppingSaleInquiryCommentRequest> = {};
  const basicRes = await api.functional.shoppings.sellers.sales.reviews.comments.patch(
