import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSaleInquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IShoppingSaleInquiryComment as InquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import typia from "typia";

export async function test_update_review_comment_success_and_not_owner_or_invalid_id(
  connection: api.IConnection
): Promise<void> {
  // :    fixture       .
  //     API   fixture  
  const ownerSaleId = "00000000-0000-0000-0000-000000000001";
  const notOwnerSaleId = "00000000-0000-0000-0000-000000000002";
  const inquiryId = "00000000-0000-0000-0000-000000001000";
  const commentId = "00000000-0000-0000-0000-000010000000";
  const invalidCommentId = "ffffffff-ffff-ffff-ffff-ffffffffffff";

  // 1.        () 
  const before = await api.functional.shoppings.sellers.sales.reviews.comments.get(
    connection,
    ownerSaleId,
    inquiryId,
    commentId
  );
  typia.assert<InquiryComment>(before);
  TestValidator.equals("before.id = commentId")<string>(commentId)(before.id);
  //  snapshots  
  const prevSnapshotsLength = before.snapshots.length;

  // 2.   -    
  const update: InquiryComment.ICreate = {
    format: "txt",
    body: "   ",
    files: [],
  };
  const updated = await api.functional.shoppings.sellers.sales.reviews.comments.put(
    connection,
    ownerSaleId,
    inquiryId,
    commentId,
    update
  );
  typia.assert<InquiryComment.ISnapshot>(updated);
  TestValidator.equals("body updated")<string>(update.body)(updated.body);
  // snapshot  1  
  const after = await api.functional.shoppings.sellers.sales.reviews.comments.get(
    connection,
    ownerSaleId,
    inquiryId,
    commentId
  );
  typia.assert<InquiryComment>(after);
  TestValidator.equals("snapshots length increased by 1")<number>(prevSnapshotsLength + 1)(after.snapshots.length);

  // 3.        -> 403/404  
  await TestValidator.httpError("not owner forbidden or not found")(403,404)(async () =>
    api.functional.shoppings.sellers.sales.reviews.comments.put(
      connection,
      notOwnerSaleId,
      inquiryId,
      commentId,
      update
    )
  );

  // 4. /   id PUT  -> 404  
  await TestValidator.httpError("invalid comment id")(404,400)(async () =>
    api.functional.shoppings.sellers.sales.reviews.comments.put(
      connection,
      ownerSaleId,
      inquiryId,
      invalidCommentId,
      update
    )
  );
}

