import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IPageIShoppingSaleUnitStockSupplement } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleUnitStockSupplement";
import { IShoppingSaleUnitStockSupplement } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleUnitStockSupplement";
import { IShoppingSaleUnitStockSupplement_IRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleUnitStockSupplement.IRequest";

export async function test_api_list_all_supplements_for_stock_unit_with_conditions(connection: api.IConnection): Promise<void> {
    // Prerequisites: Assume existence of saleId/unitId/stockId with at least two supplements
    const saleId = "test-sale-id-1234";
    const unitId = "test-unit-id-1234";
    const stockId = "test-stock-id-1234";

    // 1. List all supplements (no filter)
    const resAll = await api.functional.shoppings.sellers.sales.units.stocks.supplements.patch(
        connection,
        saleId,
        unitId,
        stockId,
        {
            sort: null,
            page: null,
            limit: null,
        } as IShoppingSaleUnitStockSupplement_IRequest
    );
    typia.assert<IPageIShoppingSaleUnitStockSupplement>(resAll);
    TestValidator.predicate("should have supplements")(() => Array.isArray(resAll.data));

    // 2. Pagination: limit 1
    const resPage = await api.functional.shoppings.sellers.sales.units.stocks.supplements.patch(
        connection,
        saleId,
        unitId,
        stockId,
        {
            sort: null,
            page: 0,
            limit: 1,
        }
    );
    typia.assert<IPageIShoppingSaleUnitStockSupplement>(resPage);
    TestValidator.equals("limit = 1 should return a single record")<IShoppingSaleUnitStockSupplement[]>(
        [resAll.data[0]]
    )(resPage.data);
    TestValidator.equals("pagination info: current page")(
        0
    )(resPage.pagination.current);
    TestValidator.equals("pagination info: limit")(
        1
    )(resPage.pagination.limit);

    // 3. Sorting: descending by created_at
    const resSorted = await api.functional.shoppings.sellers.sales.units.stocks.supplements.patch(
        connection,
        saleId,
        unitId,
        stockId,
        {
            sort: ["-created_at"],
            page: 0,
            limit: null,
        }
    );
    typia.assert<IPageIShoppingSaleUnitStockSupplement>(resSorted);
    // Check that first item's created_at >= second if more than one record
    if (resSorted.data.length > 1) {
        TestValidator.predicate("Sorted descending by created_at")(
            () => new Date(resSorted.data[0].created_at) >= new Date(resSorted.data[1].created_at),
        );
    }

    // 4. Unauthorized access: simulate with connection lacking seller rights
    await TestValidator.httpError("Unauthorized access should fail")(401)(async () => {
        const fakeConnection = { ...connection, headers: { ...connection.headers, authorization: "Bearer invalid" } };
        await api.functional.shoppings.sellers.sales.units.stocks.supplements.patch(
            // @ts-expect-error - purposely pass bad connection
            fakeConnection,
            saleId,
            unitId,
            stockId,
            { sort: null, page: null, limit: null }
        );
    });

    // 5. Empty result: stockId with no supplements
    const emptyStockId = "test-stock-no-supplement";
    const resEmpty = await api.functional.shoppings.sellers.sales.units.stocks.supplements.patch(
        connection,
        saleId,
        unitId,
        emptyStockId,
        { sort: null, page: null, limit: null }
    );
    typia.assert<IPageIShoppingSaleUnitStockSupplement>(resEmpty);
    TestValidator.equals("should be empty array")<IShoppingSaleUnitStockSupplement[]>(
        [] as IShoppingSaleUnitStockSupplement[]
    )(resEmpty.data);
}

