export async function test_api_list_categories_for_channel_with_children_and_filters(
  connection: api.IConnection
): Promise<void> {
  // Assume channelCode exists as "test-channel" with categories and children
  const channelCode = "test-channel";

  // 1. Happy Path: Fetch with default (no filter)
  const categories: IShoppingChannelCategory.IHierarchical[] = await api.functional.shoppings.sellers.systematic.channels.categories.patch(
    connection,
    channelCode
    // No search/filter/sort body provided
  );
  typia.assert(categories);
