    TestValidator.predicate("Category has id")(() => typeof cat.id === "string" || typeof cat.id === "number");
    TestValidator.predicate("Category has children array")(() => Array.isArray(cat.children));
  }

  // 2. Paging/Filtering: Search for name includes "Test" (assuming name field exists)
  const request: IShoppingChannelCategory.IRequest = {
    search: { name: "Test" },
    paging: { limit: 2, page: 1 },
    sort: { seq: "ASC" }
  };
