import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannel";
import { IShoppingChannelCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannelCategory";
import { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import typia from "typia";

function validateCategoryTree(categories: IShoppingChannelCategory.IHierarchical[], parentId: string | null, traversed: Set<string> = new Set()): void {
    for (const category of categories) {
        typia.assert<IShoppingChannelCategory.IHierarchical>(category);
        TestValidator.equals("parent_id linkage")(parentId)(category.parent_id);
        TestValidator.predicate("unique category id")(() => !traversed.has(category.id));
        traversed.add(category.id);
        //    
        validateCategoryTree(category.children, category.id, traversed);
    }
}

export async function test_api_channel_list_channels_with_hierarchical_categories(connection: api.IConnection): Promise<void> {
