import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSection } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSection";
import { IShoppingSection as IShoppingSectionNamespace } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSection";
import { IPageIShoppingSection } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSection";
import typia from "typia";

export async function test_api_shopping_section_list_with_paging_and_permission_error(connection: api.IConnection): Promise<void> {
  // 1.     
  const req: IShoppingSectionNamespace.IRequest = {};
  const result = await api.functional.shoppings.sellers.systematic.sections.patch(connection, { body: req });
  typia.assert<IPageIShoppingSection>(result);
  typia.assert<IShoppingSection[]>(result.data);
  TestValidator.predicate("data array length matches total records")(result.data.length <= result.pagination.records);

  // 2. Paging - limit, page (limit: 1, page: 0)
  const pagingReq: IShoppingSectionNamespace.IRequest = { limit: 1, page: 0 };
  const pagingResult = await api.functional.shoppings.sellers.systematic.sections.patch(connection, { body: pagingReq });
  typia.assert<IPageIShoppingSection>(pagingResult);
  TestValidator.equals("page 0", undefined)(pagingResult.pagination.current)(0);
  TestValidator.equals("limit 1", undefined)(pagingResult.pagination.limit)(1);
  TestValidator.predicate("max 1 data")(pagingResult.data.length <= 1);

  // 3.   (   code )
  if (result.data.length > 0) {
    const sample = result.data[0];
    const searchReq: IShoppingSectionNamespace.IRequest = {
      search: { code: sample.code, name: null },
    };
    const searchResult = await api.functional.shoppings.sellers.systematic.sections.patch(connection, { body: searchReq });
    typia.assert<IPageIShoppingSection>(searchResult);
    //   code  
    for (const sec of searchResult.data) {
      TestValidator.equals("filtered section code")(sample.code)(sec.code);
    }
  }

  // 4.    (code /)
  const sortReqAsc: IShoppingSectionNamespace.IRequest = { sort: ["+section.code"] };
  const sortAscResult = await api.functional.shoppings.sellers.systematic.sections.patch(connection, { body: sortReqAsc });
  const ascCodes = sortAscResult.data.map(s => s.code);
  for (let i = 1; i < ascCodes.length; ++i)
    TestValidator.predicate("ascending code sort")(ascCodes[i] >= ascCodes[i-1]);

  const sortReqDesc: IShoppingSectionNamespace.IRequest = { sort: ["-section.code"] };
  const sortDescResult = await api.functional.shoppings.sellers.systematic.sections.patch(connection, { body: sortReqDesc });
  const descCodes = sortDescResult.data.map(s => s.code);
  for (let i = 1; i < descCodes.length; ++i)
    TestValidator.predicate("descending code sort")(descCodes[i] <= descCodes[i-1]);

  // 5.  :    
  const fakeConnection: api.IConnection = { ...connection, headers: { ...connection.headers, authorization: "Bearer invalidtoken" } };
  await TestValidator.httpError("should fail with invalid token")(401)(
    () => api.functional.shoppings.sellers.systematic.sections.patch(fakeConnection, { body: {} })
  );
}

