import { IShoppingSaleInquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IShoppingSaleInquiryComment_ICreate } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IShoppingSaleInquiryComment_IRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IPageIShoppingSaleInquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleInquiryComment";
import typia from "typia";

export async function test_api_review_comment_list_patch(connection: api.IConnection): Promise<void> {
  // 1. Create an order directly
  const goodId = "00000000-0000-4000-8000-000000000001";
  const saleId = "00000000-0000-4000-8000-000000000002";
  const unitId = "00000000-0000-4000-8000-000000000003";
  const stockId = "00000000-0000-4000-8000-000000000004";

  const createOrderInput = {
    sale_id: saleId,
    stocks: [
      {
        unit_id: unitId,
        stock_id: stockId,
        choices: [] as IShoppingCartCommodityStockChoice.ICreate[],
        quantity: 1,
      },
    ],
    volume: 1,
    accumulate: false,
  };
  const order: IShoppingOrder = await api.functional.shoppings.customers.orders.direct.post(
    connection,
    createOrderInput
  );
  typia.assert<IShoppingOrder>(order);
  TestValidator.predicate("Order should contain goods")(order.goods.length > 0);

  // 2. Write a review article
  const reviewInput: IShoppingSaleReview_ICreate = {
    good_id: order.goods[0].id,
    score: 95,
    format: "txt",
    title: "Test Review Title",
    body: "Test review content",
    files: [],
  };
  const review: IShoppingSaleReview = await api.functional.shoppings.customers.sales["{saleId}"].reviews.post(
    connection,
    saleId,
    reviewInput
  );
  typia.assert<IShoppingSaleReview>(review);

  const inquiryId = review.id;

  // 3. Check comment list initially empty
  const req: IShoppingSaleInquiryComment_IRequest = {};
  const listEmpty: IPageIShoppingSaleInquiryComment = await api.functional.shoppings.customers.sales["{saleId}"].reviews["{inquiryId}"].comments.patch(
    connection,
    saleId,
    inquiryId,
    req
  );
  typia.assert<IPageIShoppingSaleInquiryComment>(listEmpty);
  TestValidator.equals("empty comment list")([] as IShoppingSaleInquiryComment[])(listEmpty.data);

  // 4. Register multiple comments
  const commentInputs: IShoppingSaleInquiryComment_ICreate[] = [
    {
      format: "txt",
      body: "Comment #1",
      files: [],
    },
    {
      format: "txt",
      body: "Comment #2",
      files: [],
    },
  ];
  const comment1: IShoppingSaleInquiryComment = await api.functional.shoppings.customers.sales["{saleId}"].reviews["{inquiryId}"].comments.post(
    connection,
    saleId,
    inquiryId,
    commentInputs[0]
  );
  const comment2: IShoppingSaleInquiryComment = await api.functional.shoppings.customers.sales["{saleId}"].reviews["{inquiryId}"].comments.post(
    connection,
    saleId,
    inquiryId,
    commentInputs[1]
  );
  typia.assert<IShoppingSaleInquiryComment>(comment1);
  typia.assert<IShoppingSaleInquiryComment>(comment2);

  // 5. Fetch the comment list again
  const listResult: IPageIShoppingSaleInquiryComment = await api.functional.shoppings.customers.sales["{saleId}"].reviews["{inquiryId}"].comments.patch(
    connection,
    saleId,
    inquiryId,
    req
  );
  typia.assert<IPageIShoppingSaleInquiryComment>(listResult);
  TestValidator.predicate("comment list has all comments")(() =>
    listResult.data.some((c) => c.id === comment1.id) &&
    listResult.data.some((c) => c.id === comment2.id)
  );

  // 6. Negative cases: invalid saleId and inquiryId
  const fakeSaleId = "00000000-0000-4000-8000-ffffffffffff";
  const fakeInquiryId = "00000000-0000-4000-8000-eeeeeeeeeeee";

  await TestValidator.httpError("invalid saleId")(404)(() =>
    api.functional.shoppings.customers.sales["{saleId}"].reviews["{inquiryId}"].comments.patch(
      connection,
      fakeSaleId,
      inquiryId,
      req
    )
  );
  await TestValidator.httpError("invalid inquiryId")(404)(() =>
    api.functional.shoppings.customers.sales["{saleId}"].reviews["{inquiryId}"].comments.patch(
      connection,
      saleId,
      fakeInquiryId,
      req
    )
  );
}

