import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingCartCommodity } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCartCommodity";
import { IShoppingCartCommodityStock } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCartCommodityStock";
import { IShoppingOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingOrder";
import { IShoppingSaleReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleReview";
import { IShoppingSaleReview_IUpdate } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleReview";
import { IShoppingSaleReview_ICreate } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleReview";
import { IAttachmentFile_ICreate } from "@ORGANIZATION/PROJECT-api/lib/structures/IAttachmentFile";
import typia from "typia";

import { randomUUID } from "crypto";

export async function test_api_review_update_review_by_id(connection: api.IConnection, otherConnection: api.IConnection): Promise<void> {
  // 1.   :   
  const commodityStock: IShoppingCartCommodityStock.ICreate = {
    unit_id: randomUUID(),
    stock_id: randomUUID(),
    choices: [],
    quantity: 1,
  };
  const commodityCreate: IShoppingCartCommodity.ICreate = {
    sale_id: randomUUID(), //    sale_id 
    stocks: [commodityStock],
    volume: 1,
    accumulate: false,
  };
  const order: IShoppingOrder = await api.functional.shoppings.customers.orders.direct.post(
    connection,
    commodityCreate,
  );
  typia.assert<IShoppingOrder>(order);

  // 2.   good_id    
  const good = order.goods[0];
  const reviewCreate: IShoppingSaleReview.ICreate = {
    good_id: good.id,
    score: 80,
    format: "txt",
    title: " .",
    body: "  ",
    files: [] as IAttachmentFile_ICreate[],
  };
  const review = await api.functional.shoppings.customers.sales.reviews.post(
    connection,
    order.goods[0].commodity.sale.id,
    reviewCreate,
  );
  typia.assert<IShoppingSaleReview>(review);
  const reviewId = review.id;
  const saleId = order.goods[0].commodity.sale.id;

  // 3.     
  const updateInput: IShoppingSaleReview.IUpdate = {
    score: 90,
    format: "md",
    title: "  ",
    body: "  ",
    files: [] as IAttachmentFile_ICreate[],
  };
  const updatedSnapshot = await api.functional.shoppings.customers.sales.reviews.postById(
    connection,
    saleId,
    reviewId,
    updateInput,
  );
  typia.assert<IShoppingSaleReview.ISnapshot>(updatedSnapshot);
  TestValidator.equals("snapshot.title")("  ")(updatedSnapshot.title);
  TestValidator.equals("snapshot.score")(90)(updatedSnapshot.score);

  // 4.         (403 ) 
  await TestValidator.httpError("other user modify forbidden")(403,404)(() =>
    api.functional.shoppings.customers.sales.reviews.postById(
      otherConnection,
      saleId,
      reviewId,
      updateInput,
    ),
  );

  // 5.    id   404  
  await TestValidator.httpError("not found id")(404)(() =>
    api.functional.shoppings.customers.sales.reviews.postById(
      connection,
      saleId,
      randomUUID(),
      updateInput,
    ),
  );

  // 6.     validation  ( score -1,  )
  const invalidInputs: IShoppingSaleReview.IUpdate[] = [
    { ...updateInput, score: -1 },
    { ...updateInput, title: "" },
    { ...updateInput, body: "" },
    { ...updateInput, format: "invalid" as any },
    { ...updateInput, files: null as any }, //  files
  ];
  for (const input of invalidInputs) {
    await TestValidator.httpError("invalid input")(422,400,409)(() =>
      api.functional.shoppings.customers.sales.reviews.postById(
        connection,
        saleId,
        reviewId,
        input,
      ),
    );
  }
}

