import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingCartCommodity } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCartCommodity";
import { IShoppingCartCommodityStock } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCartCommodityStock";
import { IShoppingCartCommodityStockChoice } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCartCommodityStockChoice";
import { IShoppingOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingOrder";
import { IShoppingSaleReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleReview";
import { IShoppingSaleReview_IRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleReview.IRequest";
import { IShoppingSaleReview_ICreate } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleReview.ICreate";
import { IPageIShoppingSaleReview_IAbridge } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleReview.IAbridge";

//  uuid   (   )
function createUUID(): string {
    return "00000000-0000-4000-8000-" + Math.random().toString(16).slice(2, 14).padEnd(12,"0");
}

export async function test_api_review_abridges_patch(
    connection: api.IConnection,
): Promise<void> {
    // [1]   good  
    const sale_id = createUUID();
    const unit_id = createUUID();
    const stock_id = createUUID();
    const commodityStock: IShoppingCartCommodityStock.ICreate = {
        unit_id,
        stock_id,
        choices: [] as IShoppingCartCommodityStockChoice.ICreate[],
        quantity: 1,
    };
    const orderInput = {
        sale_id,
        stocks: [commodityStock],
        volume: 1,
        accumulate: false,
    };
    // Order 
    const order: IShoppingOrder = await api.functional.shoppings.customers.orders.direct(
        connection,
        orderInput,
    );
    typia.assert<IShoppingOrder>(order);
    const good = order.goods[0];
    // [2]    
    const abridgeReq: IShoppingSaleReview_IRequest = {};
    const emptyResult = await api.functional.shoppings.customers.sales.reviews.abridges(
        connection,
        sale_id,
        abridgeReq,
    );
    typia.assert<IPageIShoppingSaleReview_IAbridge>(emptyResult);
    TestValidator.equals("no review list should be empty")(Array.isArray(emptyResult.data) ? 0 : -1)(emptyResult.data.length);

    // [3]   
    const createReview = async (title: string, score: number) => {
        const reviewInput: IShoppingSaleReview_ICreate = {
            good_id: good.id,
            score,
            format: "txt",
            title,
            body: "review body " + title,
            files: [],
        };
        return await api.functional.shoppings.customers.sales.reviews.post(
            connection,
            sale_id,
            reviewInput,
        );
    };
    const review1 = await createReview("first review", 80);
    typia.assert<IShoppingSaleReview>(review1);
    const review2 = await createReview("second review", 100);
    typia.assert<IShoppingSaleReview>(review2);

    // [4]   
    const result = await api.functional.shoppings.customers.sales.reviews.abridges(
        connection,
        sale_id,
        abridgeReq,
    );
    typia.assert<IPageIShoppingSaleReview_IAbridge>(result);
    TestValidator.equals("review count matches")(2)(result.data.length);
    //   
    const titles = result.data.map((r) => r.title);
    TestValidator.predicate("title includes written")(titles.includes("first review") && titles.includes("second review"));
    const scores = result.data.map((r) => r.score);
    TestValidator.predicate("correct scores contain")(scores.includes(80) && scores.includes(100));

    // [5]  saleId 
    await TestValidator.httpError("no such saleId")(404)(async () => {
        await api.functional.shoppings.customers.sales.reviews.abridges(
            connection,
            createUUID(),
            abridgeReq,
        );
    });
}

