import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannel";
import { IShoppingChannelCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannelCategory";
import typia from "typia";

export async function test_api_list_categories_with_children_by_channel_code(connection: api.IConnection): Promise<void> {
    // 1.  
    const channelInput = { code: `test-channel-${Date.now()}`, name: "Test Channel" };
    const channel: IShoppingChannel = await api.functional.shoppings.admins.systematic.channels.post(connection, channelInput);
    typia.assert<IShoppingChannel>(channel);
    const channelCode = channel.code;

    // 2.    
    let categoriesEmpty: IShoppingChannelCategory.IHierarchical[] = await api.functional.shoppings.customers.systematic.channels.categories.patch(connection, channelCode, undefined);
    typia.assert<IShoppingChannelCategory.IHierarchical[]>(categoriesEmpty);
    TestValidator.equals("category list(empty)")([] as IShoppingChannelCategory.IHierarchical[])(categoriesEmpty);

    // 3.   nested  
    const parentCategoryInput = { parent_id: null, code: `parent-cat-${Date.now()}`, name: "Parent Category" };
    const parentCategory: IShoppingChannelCategory = await api.functional.shoppings.admins.systematic.channels.categories.post(connection, channelCode, parentCategoryInput);
    typia.assert<IShoppingChannelCategory>(parentCategory);

    const childCategoryInput = { parent_id: parentCategory.id, code: `child-cat-${Date.now()}`, name: "Child Category" };
    const childCategory: IShoppingChannelCategory = await api.functional.shoppings.admins.systematic.channels.categories.post(connection, channelCode, childCategoryInput);
    typia.assert<IShoppingChannelCategory>(childCategory);

    // 4.    
    const categories: IShoppingChannelCategory.IHierarchical[] = await api.functional.shoppings.customers.systematic.channels.categories.patch(connection, channelCode, undefined);
    typia.assert<IShoppingChannelCategory.IHierarchical[]>(categories);
    //  ,  children   
    const parent = categories.find((c) => c.id === parentCategory.id);
    TestValidator.predicate("parent category should exist")(() => !!parent);
    if (parent) {
      const child = parent.children.find((c) => c.id === childCategory.id);
      TestValidator.predicate("child category should exist")(() => !!child);
    }

    // 5.    
    await TestValidator.httpError("invalid channelCode should fail")(404)(() =>
        api.functional.shoppings.customers.systematic.channels.categories.patch(
            connection,
            "non-existent-code",
            undefined,
        )
    );
}

