import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannel";
import { IShoppingChannelCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannelCategory";
import { IShoppingChannelCategory as IShoppingChannelCategoryNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannelCategory";
import { IPageIShoppingChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingChannel.IHierarchical";
import typia from "typia";

export async function test_list_channels_with_categories_hierarchical(
  connection: api.IConnection,
): Promise<void> {
  // 1. (No Channel) Expect empty data
  const emptyReq = {};
  const emptyRes = await api.functional.shoppings.customers.systematic.channels.hierarchical.patch(connection, emptyReq);
  typia.assert<IPageIShoppingChannel.IHierarchical>(emptyRes);
  TestValidator.equals("no channels should return empty array")([] as IShoppingChannel.IHierarchical[])(emptyRes.data);

  // 2. Create channelA & channelB
  const channelAParam = { code: "testA", name: "Test Channel A" };
  const channelA = await api.functional.shoppings.admins.systematic.channels.post(connection, channelAParam);
  typia.assert<IShoppingChannel>(channelA);

  const channelBParam = { code: "testB", name: "Test Channel B" };
  const channelB = await api.functional.shoppings.admins.systematic.channels.post(connection, channelBParam);
  typia.assert<IShoppingChannel>(channelB);

  // 3. Add category to channelA (single depth)
  const catAParam: IShoppingChannelCategoryNS.ICreate = {
    parent_id: null, code: "catRoot", name: "Root Category A"
  };
  const catARoot = await api.functional.shoppings.admins.systematic.channels.categories.post(connection, channelA.code, catAParam);
  typia.assert<IShoppingChannelCategory>(catARoot);

  // 4. channelB: parent + child
  const catBRootParam: IShoppingChannelCategoryNS.ICreate = {
    parent_id: null, code: "catParent", name: "Root Category B"
  };
  const catBRoot = await api.functional.shoppings.admins.systematic.channels.categories.post(connection, channelB.code, catBRootParam);
  typia.assert<IShoppingChannelCategory>(catBRoot);

  const catBChildParam: IShoppingChannelCategoryNS.ICreate = {
    parent_id: catBRoot.id, code: "catChild", name: "Child Category B"
  };
  const catBChild = await api.functional.shoppings.admins.systematic.channels.categories.post(connection, channelB.code, catBChildParam);
  typia.assert<IShoppingChannelCategory>(catBChild);

  // 5. Call tree api & verify
  const treeRes = await api.functional.shoppings.customers.systematic.channels.hierarchical.patch(connection, {});
  typia.assert<IPageIShoppingChannel.IHierarchical>(treeRes);
  TestValidator.equals("2 channels")(
    [channelA.code, channelB.code].sort()
  )(
    treeRes.data.map(c => c.code).sort()
  );

  const chA = treeRes.data.find(c => c.code === channelA.code)!;
  const chB = treeRes.data.find(c => c.code === channelB.code)!;

  // channelA should have 1 root category and no children
  TestValidator.equals("channelA root categories count")(1)(chA.categories.length);
  const catARes = chA.categories[0];
  TestValidator.equals("catA root, no children")(0)(catARes.children.length);

  // channelB should have 1 root parent & its child
  TestValidator.equals("channelB root categories count")(1)(chB.categories.length);
  const catBRes = chB.categories[0];
  TestValidator.equals("catB parent category name")("Root Category B")(catBRes.name);
  TestValidator.equals("catB has 1 child")(1)(catBRes.children.length);
  const bChild = catBRes.children[0];
  TestValidator.equals("catB child name")("Child Category B")(bChild.name);

  // 6. Error: wrong parent_id
  const badCatParam: IShoppingChannelCategoryNS.ICreate = {
    parent_id: "bdbadbad-1234-4bad-baad-abcdefabcdef", code: "errorCat", name: "Bad Cat"
  };
  await TestValidator.httpError("invalid parent_id must fail")(400)(
    () => api.functional.shoppings.admins.systematic.channels.categories.post(connection, channelB.code, badCatParam)
  );
}

