import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingSection } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSection";
import { IShoppingSection as ISection } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSection";
import { IPageIShoppingSection } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSection";

export async function test_list_all_sections(connection: api.IConnection): Promise<void> {
  // 1.   
  const emptyReq = {};
  const emptyRes = await api.functional.shoppings.customers.systematic.sections.patch(connection, emptyReq);
  typia.assert<IPageIShoppingSection>(emptyRes);
  TestValidator.equals("sections - empty")(([] as ISection[]))(emptyRes.data);

  // 2.  1 
  const sectionInput1 = { code: `E2E_SEC01_${Date.now()}`, name: "  1" };
  const created1 = await api.functional.shoppings.admins.systematic.sections.post(connection, sectionInput1);
  typia.assert<ISection>(created1);

  // 3.  1   
  const oneRes = await api.functional.shoppings.customers.systematic.sections.patch(connection, {});
  typia.assert<IPageIShoppingSection>(oneRes);
  TestValidator.equals("sections - one")(([
    created1,
  ] as ISection[]))(oneRes.data);

  // 4.  2,3  
  const sectionInput2 = { code: `E2E_SEC02_${Date.now()}`, name: "  2" };
  const created2 = await api.functional.shoppings.admins.systematic.sections.post(connection, sectionInput2);
  typia.assert<ISection>(created2);

  const sectionInput3 = { code: `E2E_SEC03_${Date.now()}`, name: "  3" };
  const created3 = await api.functional.shoppings.admins.systematic.sections.post(connection, sectionInput3);
  typia.assert<ISection>(created3);

  // 5.  -  3  
  const listRes = await api.functional.shoppings.customers.systematic.sections.patch(connection, {});
  typia.assert<IPageIShoppingSection>(listRes);

  // code   ( API       )
  const expectedAll = [created1, created2, created3].sort((a, b) => a.code.localeCompare(b.code));
  const actualAll = [...listRes.data].sort((a, b) => a.code.localeCompare(b.code));
  TestValidator.equals("sections - all items & sorted")(expectedAll)(actualAll);

  // 6.  : name 
  const searchByName = { search: { name: " 2", code: null } };
  const searchRes = await api.functional.shoppings.customers.systematic.sections.patch(connection, searchByName);
  typia.assert<IPageIShoppingSection>(searchRes);
  TestValidator.equals("sections - search by name")(([
    created2,
  ] as ISection[]))(searchRes.data);

  // 7.  : code 
  const sortByCodeDesc = { sort: ["-section.code"] };
  const sortedDescRes = await api.functional.shoppings.customers.systematic.sections.patch(connection, sortByCodeDesc);
  typia.assert<IPageIShoppingSection>(sortedDescRes);
  const expectedDesc = [created1, created2, created3].sort((a, b) => b.code.localeCompare(a.code));
  TestValidator.equals("sections - sort desc")(expectedDesc)(sortedDescRes.data);

  // 8.  : limit 2,  
  const pagingReq = { limit: 2, page: 0, sort: ["+section.code"] };
  const pagingRes = await api.functional.shoppings.customers.systematic.sections.patch(connection, pagingReq);
  typia.assert<IPageIShoppingSection>(pagingRes);
  TestValidator.equals("sections - paging; page1")((expectedAll.slice(0, 2)))(pagingRes.data);
  TestValidator.predicate("sections - paging count")(() => pagingRes.pagination.records === 3 && pagingRes.pagination.pages >= 2);
}

