import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingCartCommodity } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCartCommodity";
import { IShoppingCartCommodity as ICartCommodityNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingCartCommodity";
import typia from "typia";

export async function test_api_cart_update_commodity_quantity_and_permissions(
  connection: api.IConnection,
  anotherConnection: api.IConnection,
  validSaleId: string,
  validUnitId: string,
  validStockId: string,
  stockQuantity: number,
): Promise<void> {
  // 1. Create a new cart commodity
  const createInput: ICartCommodityNS.ICreate = {
    sale_id: validSaleId,
    stocks: [
      {
        unit_id: validUnitId,
        stock_id: validStockId,
        choices: [],
        quantity: 1,
      },
    ],
    volume: 1,
    accumulate: false,
  };
  const commodity: IShoppingCartCommodity = await api.functional.shoppings.customers.carts.commodities.post(
    connection,
    createInput,
  );
  typia.assert<IShoppingCartCommodity>(commodity);
  const commodityId = commodity.id;

  // : update with valid positive volume <= stock quantity
  await api.functional.shoppings.customers.carts.commodities.put(
    connection,
    commodityId,
    { volume: Math.min(stockQuantity, 2) }, // 1  2
  );

  // : update with 0  error
  await TestValidator.httpError("volume=0 should fail")(400, 422)(async () =>
    api.functional.shoppings.customers.carts.commodities.put(
      connection,
      commodityId,
      { volume: 0 },
    )
  );

  // : update with negative value  error
  await TestValidator.httpError("volume<0 should fail")(400, 422)(async () =>
    api.functional.shoppings.customers.carts.commodities.put(
      connection,
      commodityId,
      { volume: -3 },
    )
  );

  // : update with volume > stockQuantity  error
  await TestValidator.httpError("volume>stock should fail")(410, 400)(async () =>
    api.functional.shoppings.customers.carts.commodities.put(
      connection,
      commodityId,
      { volume: stockQuantity + 100 },
    )
  );

  //   commodity id   anotherConnection  1 
  const anotherInput: ICartCommodityNS.ICreate = {
    sale_id: validSaleId,
    stocks: [
      {
        unit_id: validUnitId,
        stock_id: validStockId,
        choices: [],
        quantity: 1,
      },
    ],
    volume: 1,
    accumulate: false,
  };
  const otherCommodity: IShoppingCartCommodity = await api.functional.shoppings.customers.carts.commodities.post(
    anotherConnection,
    anotherInput,
  );
  typia.assert<IShoppingCartCommodity>(otherCommodity);

  //     commodity (update)    
  await TestValidator.httpError("update other's cart commodity forbidden")(403, 404)(async () =>
    api.functional.shoppings.customers.carts.commodities.put(
      connection,
      otherCommodity.id,
      { volume: 2 },
    )
  );
}

