import { IShoppingAddress_ICreate } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingAddress.ICreate";
import typia from "typia";

export async function test_api_publish_order_and_handle_payment_statuses(connection: api.IConnection): Promise<void> {
  // 1.  ( )
  const testCommodityId = "11111111-1111-1111-1111-111111111111"; //  UUID (cart commodity id)
  const orderInput: IShoppingOrder_ICreate = {
    goods: [
      {
        commodity_id: testCommodityId,
        volume: 1,
      } satisfies IShoppingOrderGood_ICreate,
    ],
    name: ""
  };

  const newOrder = await api.functional.shoppings.customers.orders.post(connection, orderInput);
  typia.assert<IShoppingOrder>(newOrder);
  TestValidator.predicate("order.created_at exists")(!!newOrder.created_at);
  TestValidator.equals("order.goods length")(1)(newOrder.goods.length);
  const orderId = newOrder.id;

  // 2.   
  const addressInput: IShoppingAddress_ICreate = {
    mobile: "01012341234",
    name: "",
    country: "",
    province: "",
    city: "",
    department: "",
    possession: " 123",
    zip_code: "12345",
    special_note: null
  };
  const validVendor = { code: "PAYVENDOR", uid: "ABCDEF1234567890" };
  const publishInput: IShoppingOrderPublish_ICreate = {
    address: addressInput,
    vendor: validVendor
  };
  const publishResult = await api.functional.shoppings.customers.orders.publish.post(connection, orderId, publishInput);
  typia.assert<IShoppingOrderPublish>(publishResult);
  TestValidator.predicate("publish.paid_at exists")(publishResult.paid_at === null || typeof publishResult.paid_at === "string");
  TestValidator.equals("publish.address.name")(addressInput.name)(publishResult.address.name);

  // 3.   (  )
  await TestValidator.httpError("already published order must fail")(409)(async () => {
    await api.functional.shoppings.customers.orders.publish.post(connection, orderId, publishInput);
  });

  // 4.   - vendor (vendor /null )
  const badVendorInput: IShoppingOrderPublish_ICreate = {
    address: addressInput,
    vendor: null
  };
  await TestValidator.httpError("vendor=null must fail")(400, 422)(async () => {
    await api.functional.shoppings.customers.orders.publish.post(connection, orderId, badVendorInput);
  });

  // 5.  () 
  const noAddressInput: IShoppingOrderPublish_ICreate = {
    address: null as any,
    vendor: validVendor
  };
  await TestValidator.httpError("address=null must fail")(400, 422)(async () => {
    await api.functional.shoppings.customers.orders.publish.post(connection, orderId, noAddressInput);
  });
}

