  // 1.  
  const questionInput: IShoppingSaleQuestion.ICreate = {
    secret: false,
    format: "txt",
    title: " ",
    body: " ",
    files: []
  };
  const question = await api.functional.shoppings.customers.sales.questions.post(connection, saleId, questionInput);
  typia.assert<IShoppingSaleQuestion>(question);

  const inquiryId = question.id;

  // 2.      
  const emptyRequest: IShoppingSaleInquiryComment.IRequest = {
    search: null,
    sort: null,
    page: null,
    limit: null
  };
  const emptyRes = await api.functional.shoppings.customers.sales.questions.comments.patch(connection, saleId, inquiryId, emptyRequest);
  typia.assert<IPageIShoppingSaleInquiryComment>(emptyRes);
  TestValidator.equals("comments empty")(emptyRes.data)([] as IShoppingSaleInquiryComment[]);

  // 3.    
