    ]
  };
  const comment2 = await api.functional.shoppings.customers.sales.questions.comments.post(connection, saleId, inquiryId, commentInput2);
  typia.assert<IShoppingSaleInquiryComment>(comment2);

  // 4.      
  const withCommentsRes = await api.functional.shoppings.customers.sales.questions.comments.patch(connection, saleId, inquiryId, emptyRequest);
  typia.assert<IPageIShoppingSaleInquiryComment>(withCommentsRes);
  TestValidator.equals("comments count 2")(withCommentsRes.data.length)(2);

