import { IShoppingMember } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingMember";
import typia from "typia";

export async function test_put_admin_authenticate_login_success_and_failure(connection: api.IConnection): Promise<void> {
    // 1. Create administrator account
    const joinInput: IShoppingAdministrator.IJoin = {};
    const admin = await api.functional.shoppings.admins.authenticate.post(connection, joinInput);
    typia.assert<IShoppingAdministrator.IInvert>(admin);

    // Extract email (required by login DTO)
    const emails = admin.member.emails;
    TestValidator.predicate("at least one email")(() => Array.isArray(emails) && emails.length > 0);
    const email = emails[0]?.value;
    const password = "test-password"; // use known test password, assumed from implementation

    // 2. Valid login
    const validLogin: IShoppingMember.ILogin = { email, password };
    const adminLogin = await api.functional.shoppings.admins.authenticate.login(connection, validLogin);
    typia.assert<IShoppingAdministrator.IInvert>(adminLogin);
    TestValidator.equals("admin login invert eq")<IShoppingAdministrator.IInvert>(admin)(adminLogin);

    // 3. Invalid password
    const invalidPasswordLogin: IShoppingMember.ILogin = { email, password: "wrong-password" };
    await TestValidator.httpError("admin login with wrong password should fail")(401)(async () => {
        await api.functional.shoppings.admins.authenticate.login(connection, invalidPasswordLogin);
    });

    // 4. Non-existent account
    const nonExistLogin: IShoppingMember.ILogin = { email: "not-found@e2e.local", password: "dummy" };
    await TestValidator.httpError("admin login with non-existent email should fail")(404, 401)(async () => {
        await api.functional.shoppings.admins.authenticate.login(connection, nonExistLogin);
    });
}

