  typia.assert(admin);

  // 2.     (POST /shoppings/admins/deposits)
  const code = `dep-${Math.random().toString(36).substring(2,10)}`;
  const input: IShoppingDepositCreate.ICreate = {
    code,
    source: "E2E test",
    direction: 1,
