import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingDeposit } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDeposit";
import { IShoppingDeposit as IShoppingDepositNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingDeposit";
import { IPageIShoppingDeposit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingDeposit";
import typia from "typia";

export async function test_patch_admins_deposits_list_and_pagination(connection: api.IConnection): Promise<void> {
    // 1.     
    const items: IShoppingDeposit[] = [];
    const sources = ["test-source-A", "test-source-B", "test-source-C"];
    const codes = ["abc", "def", "ghi"];
    const directions = [1, -1, 1];
    for (let i = 0; i < 3; ++i) {
        const input: IShoppingDepositNS.ICreate = {
            code: codes[i],
            source: sources[i],
            direction: directions[i] as 1 | -1,
        };
        const created = await api.functional.shoppings.admins.deposits.post(connection, input);
        typia.assert<IShoppingDeposit>(created);
        items.push(created);
    }

    // 2.      
    const totalList = await api.functional.shoppings.admins.deposits.patch(connection, {});
    typia.assert<IPageIShoppingDeposit>(totalList);
    TestValidator.equals("   ")<number>(items.length)(totalList.data.length >= 3 ? 3 : totalList.data.length);

    // 3.   : limit=2
    const pageReq: IShoppingDepositNS.IRequest = {
        limit: 2,
        page: 0,
    };
    const page0 = await api.functional.shoppings.admins.deposits.patch(connection, pageReq);
    typia.assert<IPageIShoppingDeposit>(page0);
    TestValidator.predicate("limit ")(() => page0.data.length === 2);
    TestValidator.predicate("pagination  ")(() => page0.pagination.limit === 2);

    //  1 
    const page1Req: IShoppingDepositNS.IRequest = {
        limit: 2, page: 1,
    };
    const page1 = await api.functional.shoppings.admins.deposits.patch(connection, page1Req);
    typia.assert<IPageIShoppingDeposit>(page1);
    TestValidator.predicate("page 1   ")(() =>
        page1.data.length === (items.length > 2 ? 1 : 0)
    );

    // 4.     : source 1 
    const filterSourceReq: IShoppingDepositNS.IRequest = {
        search: { source: sources[0] },
    };
    const filtered = await api.functional.shoppings.admins.deposits.patch(connection, filterSourceReq);
    typia.assert<IPageIShoppingDeposit>(filtered);
    TestValidator.predicate("source ")(() =>
      filtered.data.length === 1 && filtered.data[0].source === sources[0]
    );

    // code 
    const filterCodeReq: IShoppingDepositNS.IRequest = {
        search: { code: codes[1] },
    };
    const filteredCode = await api.functional.shoppings.admins.deposits.patch(connection, filterCodeReq);
    typia.assert<IPageIShoppingDeposit>(filteredCode);
    TestValidator.predicate("code ")(() =>
      filteredCode.data.length === 1 && filteredCode.data[0].code === codes[1]
    );

    // direction 
    const filterDirReq: IShoppingDepositNS.IRequest = {
        search: { direction: directions[2] },
    };
    const filteredDir = await api.functional.shoppings.admins.deposits.patch(connection, filterDirReq);
    typia.assert<IPageIShoppingDeposit>(filteredDir);
    TestValidator.predicate("direction ")(() =>
      filteredDir.data.every(d => d.direction === directions[2])
    );

    // 5.      
    const absentReq: IShoppingDepositNS.IRequest = {
        search: { code: "not-exist-code" },
    };
    const absent = await api.functional.shoppings.admins.deposits.patch(connection, absentReq);
    typia.assert<IPageIShoppingDeposit>(absent);
    TestValidator.equals("   ")(0)(absent.data.length);

    // 6. pagination/data   
    for (const d of totalList.data) {
      typia.assert<IShoppingDeposit>(d);
    }
    typia.assert<IPageIShoppingDeposit>(totalList);
}

