import { IPageIShoppingMileageDonation } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingMileageDonation";

export async function test_patch_admins_mileages_donations_list_and_filter(connection: api.IConnection): Promise<void> {
    // 1.   2      2  ()
    // 1
    const citizen1: IShoppingCitizen = {
        id: crypto.randomUUID(),
        name: "1",
        mobile: "01012340001",
        created_at: new Date().toISOString()
    };
    // 2
    const citizen2: IShoppingCitizen = {
        id: crypto.randomUUID(),
        name: "2",
        mobile: "01012340002",
        created_at: new Date().toISOString()
    };
    //   (1)
    const donateInput1: IDonationNS.ICreate = {
        citizen_id: citizen1.id,
        value: 2000,
        reason: " 1"
    };
    const donation1 = await api.functional.shoppings.admins.mileages.donations.post(connection, donateInput1);
    typia.assert<IShoppingMileageDonation>(donation1);
    //   (2, )
    await new Promise(r=>setTimeout(r, 1000));
    const donateInput2: IDonationNS.ICreate = {
        citizen_id: citizen2.id,
        value: 3500,
        reason: " 2"
    };
    const donation2 = await api.functional.shoppings.admins.mileages.donations.post(connection, donateInput2);
    typia.assert<IShoppingMileageDonation>(donation2);
    // 2.     ( 2)
    let listReq: IDonationNS.IRequest = {};
    const listAll = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(listAll);
    TestValidator.predicate("all donations exist")(() =>
        listAll.data.some(x => x.id === donation1.id)
        && listAll.data.some(x => x.id === donation2.id)
    );
    // 3. citizen id 
    listReq = { search: { citizen: { mobile: citizen1.mobile, name: citizen1.name } } };
    const filtered1 = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(filtered1);
    TestValidator.predicate("only citizen1 donations")(() =>
        filtered1.data.every(x => x.citizen.id === citizen1.id)
    );
    // 4. (minimum) 
    listReq = { search: { minimum: 3000 } };
    const filtered2 = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(filtered2);
    TestValidator.predicate("only >=3000 donations")(() =>
        filtered2.data.every(x => x.value >= 3000)
    );
    // 5. (maximum) 
    listReq = { search: { maximum: 2500 } };
    const filtered3 = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(filtered3);
    TestValidator.predicate("only <=2500 donations")(() =>
        filtered3.data.every(x => x.value <= 2500)
    );
    // 6.   (from, to)
    const fromDate = new Date(Date.now()-2000).toISOString();
    const toDate = new Date(Date.now()+2000).toISOString();
    listReq = { search: { from: fromDate, to: toDate } };
    const filtered4 = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(filtered4);
    //      
    TestValidator.predicate("all data in time range")(() =>
        filtered4.data.every(x => 
            new Date(x.created_at) >= new Date(fromDate) && new Date(x.created_at) <= new Date(toDate)
        )
    );
    // 7. (sort)  (, )
    listReq = { sort: ["-donation.value"] };
    const sortedDesc = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(sortedDesc);
    TestValidator.predicate("desc value order")(() =>
        sortedDesc.data.every((x,i,arr)=> i===0 || arr[i-1].value >= x.value)
    );
    listReq = { sort: ["+donation.value"] };
    const sortedAsc = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(sortedAsc);
    TestValidator.predicate("asc value order")(() =>
        sortedAsc.data.every((x,i,arr)=> i===0 || arr[i-1].value <= x.value)
    );
    // 8.      
    listReq = { search: { minimum: 99999 } };
    const none = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(none);
    TestValidator.equals("empty data when no match")([] as IShoppingMileageDonation[])(none.data);
    // 9.    
    listReq = {};
    const relist = await api.functional.shoppings.admins.mileages.donations.patch(connection, listReq);
    typia.assert<IPageIShoppingMileageDonation>(relist);
}

