// Helper: Returns a random UUID (not colliding with any present)
function random_uuid() {
  // v4 UUID pattern, not checking for actual collisions for test simplicity
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, function (c) {
    const r = (Math.random() * 16) | 0,
      v = c === "x" ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

