import { IShoppingOrder_IRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingOrder.IRequest";
import typia from "typia";

export async function test_patch_admins_orders_list_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
    // 1. Get admin info to confirm context
    const admin = await api.functional.shoppings.admins.authenticate.get(connection);
    typia.assert(admin);

    // 2. Create at least one order as a customer (simulate minimal goods)
    // Note: The creation input structure is not fully specified (e.g., what is a valid commodity_id? Here, we use a UUID placeholder.)
    // In real E2E, this should use a registered commodity from test fixtures or preceding test cases.
    const testCreateInput: IShoppingOrder_ICreate = {
        goods: [
            {
                commodity_id: "00000000-0000-0000-0000-000000000001",
                volume: 1
            }
        ],
        name: " "
    };
    const testOrder = await api.functional.shoppings.customers.orders.post(connection, testCreateInput);
    typia.assert<IShoppingOrder>(testOrder);

    // 3a. No filters/pagination: should include the created order
    const defaultRequest: IShoppingOrder_IRequest = {};
    const pageDefault = await api.functional.shoppings.admins.orders.patch(connection, defaultRequest);
    typia.assert<IPageIShoppingOrder>(pageDefault);
    TestValidator.predicate("should have newly created order")(
      () => pageDefault.data.some((o) => o.id === testOrder.id)
    );

    // 3b. Pagination: limit 1, check first page returns single order
    const pagedRequest: IShoppingOrder_IRequest = { limit: 1 };
    const pageLimited = await api.functional.shoppings.admins.orders.patch(connection, pagedRequest);
    typia.assert<IPageIShoppingOrder>(pageLimited);
    TestValidator.equals("should return 1 order for limit=1")(1)(pageLimited.data.length);

    // 3c. Filter (e.g., impossible price): expect empty results
    const priceFilterRequest: IShoppingOrder_IRequest = { search: { min_price: 99999999 } };
    const pageFilter = await api.functional.shoppings.admins.orders.patch(connection, priceFilterRequest);
    typia.assert<IPageIShoppingOrder>(pageFilter);
    TestValidator.equals("should return empty for extreme price", )(0)(pageFilter.data.length);

    // 3d. If possible, try another filter: e.g. search.paid = false
    const unpaidFilterRequest: IShoppingOrder_IRequest = { search: { paid: false } };
    const pageUnpaid = await api.functional.shoppings.admins.orders.patch(connection, unpaidFilterRequest);
    typia.assert<IPageIShoppingOrder>(pageUnpaid);
    // Can't strictly predict data contents without full system state, but check structure

    // 3e. Extreme pagination: page high number -> expect empty array if there aren't enough orders
    const highPageRequest: IShoppingOrder_IRequest = { page: 999, limit: 10 };
    const pageHigh = await api.functional.shoppings.admins.orders.patch(connection, highPageRequest);
    typia.assert<IPageIShoppingOrder>(pageHigh);
    if (pageHigh.data.length === 0) {
        TestValidator.equals("should return empty array for high page")(0)(pageHigh.data.length);
    }
}

