import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSaleInquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IShoppingSaleInquiryComment as InquiryCommentNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleInquiryComment";
import { IPageIShoppingSaleInquiryComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleInquiryComment";
import typia from "typia";

export async function test_patch_admins_sales_questions_comments_list_and_empty_case(connection: api.IConnection): Promise<void> {
    // :  saleId  inquiryId
    const saleId = "11111111-1111-4111-8111-111111111111";
    const inquiryId = "22222222-2222-4222-8222-222222222222";

    // 1.     ->    
    {
        const req: InquiryCommentNS.IRequest = {
            search: null,
            sort: null,
            page: 0,
            limit: 10,
        };
        const res = await api.functional.shoppings.admins.sales.questions.comments.patch
            (connection, saleId, inquiryId, req);
        typia.assert<IPageIShoppingSaleInquiryComment>(res);
        TestValidator.equals("empty comments array")(res.data)([] as IShoppingSaleInquiryComment[]);
    }

    // 2.  2 
    const commentInputs: InquiryCommentNS.ICreate[] = [
        {
            format: "md",
            body: " ",
            files: []
        },
        {
            format: "txt",
            body: " .",
            files: [
                {
                    name: "img",
                    extension: "jpg",
                    url: "https://example.com/image.jpg"
                }
            ]
        }
    ];
    const expected: IShoppingSaleInquiryComment[] = [];
    for (const input of commentInputs) {
        const created = await api.functional.shoppings.admins.sales.questions.comments.post(
            connection, saleId, inquiryId, input
        );
        typia.assert<IShoppingSaleInquiryComment>(created);
        expected.push(created);
    }

    // 3.   ,      
    {
        const req: InquiryCommentNS.IRequest = {
            search: null,
            sort: null,
            page: 0,
            limit: 10,
        };
        const res = await api.functional.shoppings.admins.sales.questions.comments.patch(
            connection, saleId, inquiryId, req
        );
        typia.assert<IPageIShoppingSaleInquiryComment>(res);
        TestValidator.equals("all comments count")(res.data.length)(expected.length);
        //   ID  
        for (const comment of expected) {
            const found = res.data.find((c) => c.id === comment.id);
            TestValidator.predicate(`comment ${comment.id} in response`)(!!found);
        }
    }

    // 4.   inquiryId ,   
    {
        const emptyInquiryId = "33333333-3333-4333-8333-333333333333";
        const req: InquiryCommentNS.IRequest = {
            search: null,
            sort: null,
            page: 0,
            limit: 10,
        };
        const res = await api.functional.shoppings.admins.sales.questions.comments.patch(
            connection, saleId, emptyInquiryId, req
        );
        typia.assert<IPageIShoppingSaleInquiryComment>(res);
        TestValidator.equals("empty for different inquiry")(res.data)([] as IShoppingSaleInquiryComment[]);
    }
}

