export async function test_update_admins_sale_question_comment_with_various_scenarios(connection: api.IConnection, saleId: string, inquiryId: string): Promise<void> {
  // --- :    ---
  const createInput: ICreate.ICreate = {
    format: "md",
    body: "original comment",
    files: [
      {
        name: "sample",
        extension: "jpg",
        url: "https://example.com/sample.jpg",
      },
    ],
  };
  const created = await api.functional.shoppings.admins.sales.questions.comments.post(
    connection,
    saleId,
    inquiryId,
    createInput
  );
  typia.assert<IShoppingSaleInquiryComment>(created);
  TestValidator.equals("comment body should match")(createInput.body)(created.snapshots[created.snapshots.length - 1].body);

  // --- (1)    ---
  const updateInput: ICreate.ICreate = {
    format: "md",
    body: "updated comment",
    files: [
      {
        name: "updated",
        extension: "png",
        url: "https://example.com/updated.png",
      },
    ],
  };
  const updated = await api.functional.shoppings.admins.sales.questions.comments.put(
