import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSaleQuestion } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleQuestion";
import { IPageIShoppingSaleQuestion } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleQuestion";
import typia from "typia";

export async function test_list_admins_sale_questions_with_filter_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1.     saleId (:   saleId )
  const validSaleId = "11111111-1111-1111-1111-111111111111";

  // 1-1.   ( )
  const baseReq = {};
  const baseResult = await api.functional.shoppings.admins.sales.questions.patch(
    connection,
    validSaleId,
    { body: baseReq },
  );
  typia.assert<IPageIShoppingSaleQuestion.ISummary>(baseResult);
  TestValidator.predicate("should have data & pagination")(
    baseResult.data !== undefined && baseResult.pagination !== undefined,
  );

  // 1-2.   
  const answeredReq = { search: { answered: true } };
  const answeredResult = await api.functional.shoppings.admins.sales.questions.patch(
    connection,
    validSaleId,
    { body: answeredReq },
  );
  typia.assert<IPageIShoppingSaleQuestion.ISummary>(answeredResult);
  if (answeredResult.data.length > 0) {
    TestValidator.predicate("all answered")(
      answeredResult.data.every(q => q.answer !== null),
    );
  }

  // 1-3. () 
  const titleKeyword = baseResult.data[0]?.title || "";
  const titleReq = { search: { title: titleKeyword } };
  const titleResult = await api.functional.shoppings.admins.sales.questions.patch(
    connection,
    validSaleId,
    { body: titleReq },
  );
  typia.assert<IPageIShoppingSaleQuestion.ISummary>(titleResult);
  if (titleKeyword) {
    TestValidator.predicate("title matched")(
      titleResult.data.every(q => q.title.includes(titleKeyword)),
    );
  }

  // 1-4.  ( , limit 2)
  const pagingReq = { page: 0, limit: 2 };
  const pagingResult = await api.functional.shoppings.admins.sales.questions.patch(
    connection,
    validSaleId,
    { body: pagingReq },
  );
  typia.assert<IPageIShoppingSaleQuestion.ISummary>(pagingResult);
  TestValidator.predicate("respect limit")(
    pagingResult.data.length <= 2,
  );

  // 1-5.  ()
  const sortReq = { sort: ["-created_at"] };
  const sortResult = await api.functional.shoppings.admins.sales.questions.patch(
    connection,
    validSaleId,
    { body: sortReq },
  );
  typia.assert<IPageIShoppingSaleQuestion.ISummary>(sortResult);
  if (sortResult.data.length >= 2) {
    const t0 = new Date(sortResult.data[0].created_at).getTime();
    const t1 = new Date(sortResult.data[1].created_at).getTime();
    TestValidator.predicate("sorted by created_at desc")(t0 >= t1);
  }

  // 1-6.    ( )
  const noneReq = { search: { title: "UNLIKELY_KEYWORD_@#$_9999" } };
  const noneResult = await api.functional.shoppings.admins.sales.questions.patch(
    connection,
    validSaleId,
    { body: noneReq },
  );
  typia.assert<IPageIShoppingSaleQuestion.ISummary>(noneResult);
  TestValidator.equals("should be empty")([] as IShoppingSaleQuestion.ISummary[])(noneResult.data);

  // 2.   -   saleId (/ )
  const invalidSaleId = "00000000-0000-0000-0000-000000000000";
  await TestValidator.httpError("sale id not found")(404)(async () => {
    await api.functional.shoppings.admins.sales.questions.patch(
      connection,
      invalidSaleId,
      { body: {} },
    );
  });

  //  
  const badSaleId = "not-a-uuid";
  await TestValidator.httpError("sale id format invalid")(400, 404)(async () => {
    await api.functional.shoppings.admins.sales.questions.patch(
      connection,
      badSaleId,
      { body: {} },
    );
  });
}

