import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSaleReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSaleReview";
import { IPageIShoppingSaleReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleReview";
import typia from "typia";

export async function test_list_admins_sales_reviews_with_filters_and_pagination(
  connection: api.IConnection,
  saleId: string,
): Promise<void> {
  // 1.   (    )
  const basicRequest: IShoppingSaleReview.IRequest = {};
  const basicRes = await api.functional.shoppings.admins.sales.reviews.patch(
    connection,
    saleId,
    basicRequest,
  );
  typia.assert<IPageIShoppingSaleReview.ISummary>(basicRes);
  TestValidator.predicate("basic pageination")(
    basicRes.pagination.current >= 0 && basicRes.pagination.limit > 0,
  );

  // 2. 1 2 
  const limitRequest: IShoppingSaleReview.IRequest = { limit: 2, page: 0 };
  const limitedRes = await api.functional.shoppings.admins.sales.reviews.patch(
    connection,
    saleId,
    limitRequest,
  );
  typia.assert<IPageIShoppingSaleReview.ISummary>(limitedRes);
  TestValidator.equals("limit equals")(
    limitRequest.limit,
  )(limitedRes.pagination.limit);
  TestValidator.equals("page equals")(
    limitRequest.page,
  )(limitedRes.pagination.current);

  // 3.   (created_at desc)  (sort asc)
  const sortDescRequest: IShoppingSaleReview.IRequest = {
    sort: ["-created_at"],
  };
  const sortDescRes = await api.functional.shoppings.admins.sales.reviews.patch(
    connection,
    saleId,
    sortDescRequest,
  );
  typia.assert<IPageIShoppingSaleReview.ISummary>(sortDescRes);
  const dataDesc = sortDescRes.data;
  if (dataDesc.length >= 2) {
    TestValidator.predicate("desc by created_at")(
      new Date(dataDesc[0].created_at).getTime() >=
        new Date(dataDesc[1].created_at).getTime(),
    );
  }

  const sortAscRequest: IShoppingSaleReview.IRequest = {
    sort: ["+created_at"],
  };
  const sortAscRes = await api.functional.shoppings.admins.sales.reviews.patch(
    connection,
    saleId,
    sortAscRequest,
  );
  typia.assert<IPageIShoppingSaleReview.ISummary>(sortAscRes);
  const dataAsc = sortAscRes.data;
  if (dataAsc.length >= 2) {
    TestValidator.predicate("asc by created_at")(
      new Date(dataAsc[0].created_at).getTime() <=
        new Date(dataAsc[1].created_at).getTime(),
    );
  }

  // 4. : title   ( )
  const filterTitleRequest: IShoppingSaleReview.IRequest = {
    search: { title: "Test" },
  };
  const filterTitleRes = await api.functional.shoppings.admins.sales.reviews.patch(
    connection,
    saleId,
    filterTitleRequest,
  );
  typia.assert<IPageIShoppingSaleReview.ISummary>(filterTitleRes);
  if (filterTitleRes.data.length > 0) {
    filterTitleRes.data.forEach((i) =>
      TestValidator.predicate("filtered title contains")(
        i.title.includes("Test"),
      ),
    );
  }

  // 5. : 100 (score)
  const filterScoreRequest: IShoppingSaleReview.IRequest = {
    search: { minimum: 100, maximum: 100 },
  };
  const filterScoreRes = await api.functional.shoppings.admins.sales.reviews.patch(
    connection,
    saleId,
    filterScoreRequest,
  );
  typia.assert<IPageIShoppingSaleReview.ISummary>(filterScoreRes);
  if (filterScoreRes.data.length > 0) {
    filterScoreRes.data.forEach((i) =>
      TestValidator.equals("score only 100")(100)(i.score),
    );
  }

  // 6.  (  )
  const emptyRequest: IShoppingSaleReview.IRequest = {
    search: { title: "__NO_EXIST__" },
  };
  const emptyRes = await api.functional.shoppings.admins.sales.reviews.patch(
    connection,
    saleId,
    emptyRequest,
  );
  typia.assert<IPageIShoppingSaleReview.ISummary>(emptyRes);
  TestValidator.equals("empty page")(0)(emptyRes.data.length);

  // 7.  saleId 404  
  const randomSaleId = "11111111-1111-4111-8111-111111111111";
  await TestValidator.httpError("not found 404")(404)(async () =>
    api.functional.shoppings.admins.sales.reviews.patch(
      connection,
      randomSaleId,
      {},
    ),
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPageIShoppingSaleReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSaleReview";
import typia from "typia";

export async function test_list_admins_sales_reviews_abridged(connection: api.IConnection, saleId: string): Promise<void> {
  //     (: / ,  , 10 )
  const requestBody = {
    search: null,
    sort: null,
    page: 0,
    limit: 10
  };

  // PATCH : abridged   
  const page = await api.functional.shoppings.admins.sales.reviews.abridges.patch(connection, saleId, { body: requestBody });

  //   
  typia.assert<IPageIShoppingSaleReview.IAbridge>(page);

  //    
  TestValidator.predicate("pagination field must exist")(() => !!page.pagination);
  TestValidator.predicate("data field must be array")(() => Array.isArray(page.data));

  // pagination data   
  TestValidator.equals("records == data.length or data is paginated")(
    page.pagination.records >= 0
  )(page.data.length <= requestBody.limit);

  //   abridged     
  for (const review of page.data) {
    TestValidator.predicate("review.id exists")(() => typeof review.id === "string" && review.id.length > 0);
    TestValidator.predicate("review.title exists")(() => typeof review.title === "string" && review.title.length > 0);
    TestValidator.predicate("review.score is number")(() => typeof review.score === "number");
    TestValidator.predicate("review.created_at exists")(() => typeof review.created_at === "string");
    TestValidator.predicate("review.updated_at exists")(() => typeof review.updated_at === "string");
    TestValidator.predicate("customer.id exists")(() => typeof review.customer.id === "string");
  }
}

