import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingChannelCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannelCategory";
import { IRecordMerge } from "@ORGANIZATION/PROJECT-api/lib/structures/IRecordMerge";

export async function test_merge_admins_channel_categories_normal_and_invalid_case(connection: api.IConnection): Promise<void> {
    // Step 1: Prepare channel code and unique category data
    const channelCode = "channel-1";
    const catCode1 = `cat1-${Date.now()}`;
    const catCode2 = `cat2-${Date.now()}`;
    const catName1 = "1";
    const catName2 = "2";

    // Step 2: Create two categories
    const input1: IShoppingChannelCategory.ICreate = {
        parent_id: null,
        code: catCode1,
        name: catName1,
    };
    const result1 = await api.functional.shoppings.admins.systematic.channels.categories.post(
        connection,
        channelCode,
        input1,
    );
    typia.assert<IShoppingChannelCategory>(result1);
    TestValidator.equals("category1 name")(catName1)(result1.name);

    const input2: IShoppingChannelCategory.ICreate = {
        parent_id: null,
        code: catCode2,
        name: catName2,
    };
    const result2 = await api.functional.shoppings.admins.systematic.channels.categories.post(
        connection,
        channelCode,
        input2,
    );
    typia.assert<IShoppingChannelCategory>(result2);
    TestValidator.equals("category2 name")(catName2)(result2.name);

    // Step 3: Merge cat2 into cat1 (cat1 keep, cat2 absorbed)
    const mergeInput: IRecordMerge = {
        keep: result1.id,
        absorbed: [result2.id],
    };
    await api.functional.shoppings.admins.systematic.channels.categories.merge(
        connection,
        channelCode,
        mergeInput,
    );

    // (Optional additional validation would require a categories-listing/at API)

    // Step 4: Negative test - try to merge with invalid absorbed id
    const invalidMerge: IRecordMerge = {
        keep: result1.id,
        absorbed: ["00000000-0000-0000-0000-000000000000"],
    };
    await TestValidator.httpError("invalid absorbed id")(404)(async () => {
        await api.functional.shoppings.admins.systematic.channels.categories.merge(
            connection,
            channelCode,
            invalidMerge,
        );
    });

    // Step 5: Negative test - try to merge with no absorbed ids
    const emptyAbsorbed: IRecordMerge = {
        keep: result1.id,
        absorbed: [],
    };
    await TestValidator.httpError("no absorbed ids")(400, 422)(async () => {
        await api.functional.shoppings.admins.systematic.channels.categories.merge(
            connection,
            channelCode,
            emptyAbsorbed,
        );
    });
}

