import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingChannel";
import { IPageIShoppingChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingChannel";
import typia from "typia";

export async function test_api_list_admins_channels_with_filters_and_sorting(connection: api.IConnection): Promise<void> {
  // 1.    
  const channelsInput: IShoppingChannel.ICreate[] = [
    { code: "code-aaa", name: "Alpha Channel" },
    { code: "code-bbb", name: "Beta Channel" },
    { code: "code-ccc", name: "Gamma Channel" }
  ];
  const createdChannels: IShoppingChannel[] = [];
  for (const input of channelsInput) {
    const created = await api.functional.shoppings.admins.systematic.channels.post(connection, input);
    typia.assert<IShoppingChannel>(created);
    createdChannels.push(created);
  }
  // 2.   
  const listResp1 = await api.functional.shoppings.admins.systematic.channels.patch(connection, {});
  typia.assert<IPageIShoppingChannel>(listResp1);
  //       
  for (const c of createdChannels) {
    TestValidator.predicate(`list includes channel ${c.code}`)(() =>
      listResp1.data.some((item) => item.id === c.id),
    );
  }
  // 3.  (code)
  const searchByCodeReq = {
    search: { code: createdChannels[0].code, name: null },
    sort: null,
    page: null,
    limit: null,
  };
  const listByCode = await api.functional.shoppings.admins.systematic.channels.patch(connection, searchByCodeReq);
  typia.assert<IPageIShoppingChannel>(listByCode);
  TestValidator.equals("search by code count")([createdChannels[0]])(listByCode.data);
  // 4.  (name)
  const searchByNameReq = {
    search: { code: null, name: createdChannels[1].name },
    sort: null,
    page: null,
    limit: null,
  };
  const listByName = await api.functional.shoppings.admins.systematic.channels.patch(connection, searchByNameReq);
  typia.assert<IPageIShoppingChannel>(listByName);
  TestValidator.equals("search by name count")([createdChannels[1]])(listByName.data);

  // 5.    (code, name, created_at)
  const sortFields = ["code", "name", "created_at"] as const;
  for (const field of sortFields) {
    // asc
    const ascRes = await api.functional.shoppings.admins.systematic.channels.patch(connection, {
      sort: [`+channel.${field}`], search: null, page: null, limit: null,
    });
    typia.assert<IPageIShoppingChannel>(ascRes);
    const ascValues = ascRes.data.map((c) => c[field]);
    TestValidator.predicate(`asc sorted ${field}`)(() =>
      ascValues.every((v, i, arr) => i === 0 || String(v) >= String(arr[i - 1])),
    );
    // desc
    const descRes = await api.functional.shoppings.admins.systematic.channels.patch(connection, {
      sort: [`-channel.${field}`], search: null, page: null, limit: null,
    });
    typia.assert<IPageIShoppingChannel>(descRes);
    const descValues = descRes.data.map((c) => c[field]);
    TestValidator.predicate(`desc sorted ${field}`)(() =>
      descValues.every((v, i, arr) => i === 0 || String(v) <= String(arr[i - 1])),
    );
  }
  // 6.  
  const pageReq = { page: 0, limit: 2, search: null, sort: ["+channel.code"]};
  const pageRes = await api.functional.shoppings.admins.systematic.channels.patch(connection, pageReq);
  typia.assert<IPageIShoppingChannel>(pageRes);
  TestValidator.predicate("pagination result")(pageRes.data.length <= 2);

  // 7. + 
  const nameLike = createdChannels[2].name;
  const searchPageReq = { search: { code: null, name: nameLike }, page: 0, limit: 1, sort: null };
  const searchPageRes = await api.functional.shoppings.admins.systematic.channels.patch(connection, searchPageReq);
  typia.assert<IPageIShoppingChannel>(searchPageRes);
  TestValidator.predicate("search + pagination results")(searchPageRes.data.length === 1 && searchPageRes.data[0].id === createdChannels[2].id);

  // 8.  /           
  const invalidSearchReq = { search: { code: "not-exist-xyz", name: null }, sort: null, page: null, limit: null };
  const invalidRes = await api.functional.shoppings.admins.systematic.channels.patch(connection, invalidSearchReq);
  typia.assert<IPageIShoppingChannel>(invalidRes);
  TestValidator.equals("invalid search returns empty")([] as IShoppingChannel[])(invalidRes.data);
}

