export async function test_api_merge_admins_channels_normal_and_abnormal_case(
    connection: api.IConnection,
): Promise<void> {
    // 1. : 3  
    const input1 = { code: "chan-1", name: "Channel One" };
    const input2 = { code: "chan-2", name: "Channel Two" };
    const input3 = { code: "chan-3", name: "Channel Three" };

    const ch1 = await api.functional.shoppings.admins.systematic.channels.post(
        connection, input1,
    );
    const ch2 = await api.functional.shoppings.admins.systematic.channels.post(
        connection, input2,
    );
    const ch3 = await api.functional.shoppings.admins.systematic.channels.post(
        connection, input3,
    );

    typia.assert<IShoppingChannel>(ch1);
    typia.assert<IShoppingChannel>(ch2);
    typia.assert<IShoppingChannel>(ch3);

    // 2.  : ch2, ch3 -> ch1
    const mergeReq: IRecordMerge = {
        keep: ch1.id,
        absorbed: [ch2.id, ch3.id],
    };
    await api.functional.shoppings.admins.systematic.channels.merge(
        connection, mergeReq,
    ); //  (null) =>   

    // 3.  :   channel absorbed 
    const badMergeReq1: IRecordMerge = {
        keep: ch1.id,
        absorbed: [
            "00000000-0000-0000-0000-000000000000", //   UUID
        ],
    };
