    const childB = await api.functional.shoppings.admins.systematic.channels.categories.post(connection, channel.code, { body: childBInput });
    typia.assert<IShoppingChannelCategory>(childB);

    // 3.  /    
    const req = { search: null, sort: null, page: null, limit: null };
    const res = await api.functional.shoppings.admins.systematic.channels.hierarchical.patch(connection, { body: req });
    typia.assert<IPageIShoppingChannel.IHierarchical>(res);
    //        
    const foundTestChannel = res.data.find((c) => c.code === channel.code);
    TestValidator.predicate("  ")(() => !!foundTestChannel);
    // Food, Electronics  root  ,   
    const foodCategory = foundTestChannel?.categories.find((cat) => cat.code.startsWith("food-"));
    const electronicsCategory = foundTestChannel?.categories.find((cat) => cat.code.startsWith("electronics-"));
    TestValidator.predicate("Food  ")(() => !!foodCategory);
    TestValidator.predicate("Electronics  ")(() => !!electronicsCategory);
    // Food > Meat  
    const meatCategory = foodCategory?.children.find((cat) => cat.code.startsWith("meat-"));
    TestValidator.predicate("Food>Meat ")(() => !!meatCategory);
    // Food > Meat > Frozen
    const frozenCategory = meatCategory?.children.find((cat) => cat.code.startsWith("frozen-"));
    TestValidator.predicate("Meat>Frozen ")(() => !!frozenCategory);
    // Electronics > Notebook
    const notebookCategory = electronicsCategory?.children.find((cat) => cat.code.startsWith("notebook-"));
    TestValidator.predicate("Electronics>Notebook ")(() => !!notebookCategory);

    // 4.  :   parent_id    
