test/features/api/section/test_list_admi:
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IShoppingSection } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSection";
import { IPageIShoppingSection } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSection";

export async function test_list_admins_sections_with_search_and_filters(connection: api.IConnection): Promise<void> {
    // 1. Create multiple sections
    const sections: IShoppingSection[] = [];
    const inputs = [
        { code: "FRT", name: "Fruit Corner" },
        { code: "MEAT", name: "Butcher Corner" },
        { code: "DAIRY", name: "Dairy Section" },
    ];
    for (const input of inputs) {
        const created = await api.functional.shoppings.admins.systematic.sections.post(connection, input);
        typia.assert<IShoppingSection>(created);
        sections.push(created);
    }

    // 2. List all sections, no filters
    const pageAll = await api.functional.shoppings.admins.systematic.sections.patch(connection, {});
    typia.assert<IPageIShoppingSection>(pageAll);
    TestValidator.index("full section list")(sections)(pageAll.data);

    // 3. Search by code
    const codeSearch = await api.functional.shoppings.admins.systematic.sections.patch(connection, { search: { code: "FRT", name: null } });
    typia.assert<IPageIShoppingSection>(codeSearch);
    TestValidator.equals("code search length")(1)(codeSearch.data.length);
    TestValidator.equals("code search result")(sections[0])(codeSearch.data[0]);

    // 4. Search by name
    const nameSearch = await api.functional.shoppings.admins.systematic.sections.patch(connection, { search: { code: null, name: "Butcher Corner" } });
    typia.assert<IPageIShoppingSection>(nameSearch);
    TestValidator.equals("name search length")(1)(nameSearch.data.length);
    TestValidator.equals("name search result")(sections[1])(nameSearch.data[0]);

    // 5. Sort by code ascending
    const sortedAsc = await api.functional.shoppings.admins.systematic.sections.patch(connection, { sort: ["+section.code"] });
    typia.assert<IPageIShoppingSection>(sortedAsc);
    const expectAsc = [...sections].sort((a, b) => a.code.localeCompare(b.code));
    TestValidator.index("sort code asc")(expectAsc)(sortedAsc.data);

    // 6. Sort by code descending
    const sortedDesc = await api.functional.shoppings.admins.systematic.sections.patch(connection, { sort: ["-section.code"] });
    typia.assert<IPageIShoppingSection>(sortedDesc);
    const expectDesc = [...sections].sort((a, b) => b.code.localeCompare(a.code));
    TestValidator.index("sort code desc")(expectDesc)(sortedDesc.data);

    // 7. Pagination test (limit 2, first page)
    const paged = await api.functional.shoppings.admins.systematic.sections.patch(connection, { page: 0, limit: 2 });
    typia.assert<IPageIShoppingSection>(paged);
    TestValidator.equals("pagination length")(2)(paged.data.length);
    TestValidator.equals("pagination current")(0)(paged.pagination.current);
    TestValidator.equals("pagination limit")(2)(paged.pagination.limit);
    // The records in paged.data must be found in sections
    for (const rec of paged.data) {
        TestValidator.predicate("paged section present")(() => sections.some(s => s.id === rec.id));
    }

    // 8. Search with no matching result
    const none = await api.functional.shoppings.admins.systematic.sections.patch(connection, { search: { code: "NOPE", name: null } });
    typia.assert<IPageIShoppingSection>(none);
    TestValidator.equals("empty search result")(0)(none.data.length);
}

