test/features/api/sale/test_list_sales_d:
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IShoppingSale } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSale";
import { IShoppingSale as IRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/IShoppingSale.IRequest";
import { IPageIShoppingSale } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIShoppingSale";
import typia from "typia";

export async function test_api_sale_list_sales_details_with_various_filters(
  sellerConnection: api.IConnection,
  customerConnection: api.IConnection,
): Promise<void> {
  // 1.   (): page=1, limit=10, 
  const reqDefault: IRequest = {
    page: 1,
    limit: 10,
  };
  const resDefault = await api.functional.shoppings.sellers.sales.details.patch(
    sellerConnection,
    reqDefault,
  );
  typia.assert<IPageIShoppingSale>(resDefault);
  TestValidator.predicate("pagination page=1")(resDefault.pagination.current === 1);
  TestValidator.predicate("limit=10")(resDefault.pagination.limit === 10);
  TestValidator.predicate("data array type")(() => Array.isArray(resDefault.data));
  if (resDefault.data.length) {
    typia.assert<IShoppingSale>(resDefault.data[0]);
  }

  // 2.  : sale.created_at ,   
  for (const sort of ["+sale.created_at", "-sale.created_at"] as const) {
    const reqSort: IRequest = {
      ...reqDefault,
      sort: [sort],
    };
    const resSort = await api.functional.shoppings.sellers.sales.details.patch(
      sellerConnection,
      reqSort,
    );
    typia.assert<IPageIShoppingSale>(resSort);
    const arr = resSort.data;
    if (arr.length > 1) {
      const cmp = sort.startsWith("+")
        ? (a: IShoppingSale, b: IShoppingSale) => a.created_at.localeCompare(b.created_at)
        : (a: IShoppingSale, b: IShoppingSale) => b.created_at.localeCompare(a.created_at);
      for (let i = 1; i < arr.length; ++i)
        TestValidator.predicate(` : ${sort}`)(cmp(arr[i - 1], arr[i]) <= 0);
    }
  }

  // 3. page/limit, ,  
  const reqLastPage: IRequest = {
    ...reqDefault,
    page: resDefault.pagination.pages,
    limit: 1,
  };
  const resLastPage = await api.functional.shoppings.sellers.sales.details.patch(
    sellerConnection,
    reqLastPage,
  );
  typia.assert<IPageIShoppingSale>(resLastPage);
  TestValidator.predicate(" ")(resLastPage.pagination.current === resDefault.pagination.pages);

  // ( page,     - negative case)
  const reqInvalidPage: IRequest = {
    page: -5,
    limit: 10,
  };
  await TestValidator.httpError("   ", 400, 422)(() =>
    api.functional.shoppings.sellers.sales.details.patch(sellerConnection, reqInvalidPage),
  );

  // 4.    (  page )
  const reqNonExistPage: IRequest = {
    page: 99999,
    limit: 10,
  };
  const resNonExistPage = await api.functional.shoppings.sellers.sales.details.patch(
    sellerConnection,
    reqNonExistPage,
  );
  typia.assert<IPageIShoppingSale>(resNonExistPage);
  TestValidator.equals("empty array")<IShoppingSale[]>([])(resNonExistPage.data);

  // 5.    ( connection)
  await TestValidator.httpError(" ", 403, 401)(() =>
    api.functional.shoppings.sellers.sales.details.patch(customerConnection, reqDefault),
  );
}

