export async function test_login_seller_with_valid_and_invalid_credentials(connection: api.IConnection): Promise<void> {
    // 1.  :   
    const joinInput: IShoppingSeller.IJoin = {};
    const seller = await api.functional.shoppings.sellers.authenticate.post(connection, joinInput);
    typia.assert<IShoppingSeller.IInvert>(seller);
    const validEmail = seller.member.emails[0]?.value;

    //              
    const validPassword = "Test1234!@";
    //       (   /     )

    // 2.   
    const loginInput: IShoppingMember.ILogin = { email: validEmail, password: validPassword };
    const loginResult = await api.functional.shoppings.sellers.authenticate.login.put(connection, loginInput);
    typia.assert<IShoppingSeller.IInvert>(loginResult);
    TestValidator.equals("   ")(seller)(loginResult);

    // 3.   
    const wrongPasswordInput: IShoppingMember.ILogin = { email: validEmail, password: "wrongPassword" };
    await TestValidator.httpError("   ")(401, 403)(async () => {
        await api.functional.shoppings.sellers.authenticate.login.put(connection, wrongPasswordInput);
    });

    // 4.    
    const nonexistentInput: IShoppingMember.ILogin = { email: "not-exist-user@example.com", password: "somepass!" };
    await TestValidator.httpError("    ")(401, 403)(async () => {
        await api.functional.shoppings.sellers.authenticate.login.put(connection, nonexistentInput);
    });

    // 5.  5     (  )
    let locked = false;
    for (let i = 0; i < 6; ++i) {
        try {
            await TestValidator.httpError("    ")(401, 403, 423)(async () => {
                await api.functional.shoppings.sellers.authenticate.login.put(connection, wrongPasswordInput);
            });
        } catch (err) {
            // 423 Locked     
            locked = true;
            break;
        }
    }
    TestValidator.predicate("  ")(locked === true);
}

